body {
    background: #FFFFFF;
    color: #000000;
    font-size: 0.8em;
    margin: 1em;;
    font-family: helvetica;
    text-align: justify;
}

a { 
    color: #0087FF;
    text-decoration: none;
    border: 0;
}

p {
    margin: 0em 0em 1em 0em;
}

div#header {
    display: none;
}

div#nodeDecoration {
    font-size: 2em;
    padding: 0.1em;
    border-bottom: 0.1em solid #0087FF;
    text-align: left;
    margin: 0.5em 0em 0.5em 0em;
    color: #0087FF;
    font-weight: bolder;
    clear: both;
}

