/*Listas (están al final debido a un problema con IE)*/

/*Imágenes*/

img {
    margin: 0.5em;
}

.imagen_ampliable {
    margin: 0.5em;
    padding:0em 0.5em 0em 2em;
    
}

a .imagen_ampliable {
    border: 0;
}

.titulo {
    text-align: center;
    font-weight: bold;
}
.credenciales {
    text-align: center;
    font-size: xx-small;
}

/*Texto en general*/
.texto_izquierda { text-align: left; }
.texto_derecha { text-align: right; }
.texto_centrado { text-align: center; }
.texto_justificado { text-align: justify; }

/*Elementos en general*/
.elemento_izquierda {
    float: left;
}
.elemento_derecha {
    float: right;
}
.elemento_centrado {
    text-align: center; /*Esto se pone para que centre el IE6*/
    display: block;
    width: 100%;
    margin: auto ;
}

/*Código*/
.codigo {
	
	padding-left: 4em;
	padding-right: 2em;
}
.codigo div{
        padding-top: 1.8em;
        padding-bottom: 1.8em;
}
pre {
	color: #000000;
	margin-top:0;
	margin-bottom:0;
	font-size: 12px;
	font-family: "DejaVu Sans Mono"; 
	line-height: 20px;	
}
p code {
	font-size: 12px;
	font-family: "DejaVu Sans Mono"; 
	font-weight: bold;	
}


/*Tablas*/
table {
    font-family: helvetica;
    font-size: 1em;
}
table[align="center"] {
	margin-left:auto;
	margin-right:auto;
}

.tabla {
	margin:auto;
}

.tabla table, .tabla th, .tabla td {
	border: 0.1em solid #D4E0EE;
	border-collapse: collapse;
	font-family: helvetica, Arial, sans-serif;
	color: #555555;
}

.tabla caption {
	font-size: 125%;
	font-weight: bold;
	margin: 0.5em;
}

.tabla td, .tabla th {
	padding: 0.5em;
}

.tabla thead th {
	text-align: center;
	background: #C3DCF7;
	color: #153152;
	font-size: 100% !important;
}

.tabla tbody th {
	font-weight: bold;
	color: #153152;
	background: #C3DCF7;
	text-align: center;
}

.tabla tbody tr { background: #FCFDFE; }

.tabla td p, .tabla th p { margin-bottom: 0; }

/*.tabla tbody tr:odd { background: #F7F9FC; }*/

.tabla tbody tr:hover,
.tabla tfoot tr:hover {
    background-color:#C3DCF7;
    color:#153152;
}

.tabla tfoot th {
	font-weight: bold;
	color: #153152;
	background: #C3DCF7;
	text-align: center;
}

.tabla tfoot td {
	font-weight: bold;
}

.glosario:hover { 
text-decoration: none; 
color:black;
}
.glosario { 
text-decoration: none; 
color:black;
}
.tamano{font-size: xx-small;}


/*Evitamos que se monten los elementos flotantes en otro iDevice*/
.iDevice{
    clear: both;
    overflow: auto;
}

.iDevice_inner{
    overflow: auto;
}
.iDevice emphasis0{
    clear: both;
    overflow: auto;
}
div.block{
    clear: both;
    overflow: auto;
}

