css:
@keyframes in
{
  0%
  {
    transform: scale(1);
    filter: brightness(1);
  }

  100%
  {
    border: 2px solid !important;
    transform: scale(0.97) !important;
    filter: brightness(1.05);
  }
}



a:visited,
a:visited img,
a:visited video,
a:empty:visited,
a:visited > * img,
a:visited div:empty
{
  border-color: #fd00ff !important;
}



a img,
a video,
a:empty,
a > * img,
a div:empty
{
  box-sizing: border-box;
  animation: in 0.5s 1;
  border: 4px solid !important;
  filter: brightness(1.05);
}

a:hover
{
  text-shadow: 0 0 3px;
}


img
{
  outline: none !important;
}

a, [onclick]
{
  cursor: pointer !important;
}

a:visited:hover h1,a:visited:hover h2,a:visited:hover h3,a:visited:hover h4,a:visited:hover h5,a:visited:hover h6,a:visited:hover p,a:visited:hover span, a:visited:hover
{
  color: #fd00ff !important;
}
