css:
@-moz-document url-prefix("https://e.mail.ru/") {
.threads-enabled:after{
position:absolute;

top:450px;
width:100%;
background-color: rgb(255, 255, 255);
height: 100%;
content:" ";
}

.b-datalist {
    z-index: 1;
    bottom: 40px;
}

.b-sticky{
    position: absolute;
    z-index: 1;
    background-color: white;
    margin-bottom: 2px;
}

.b-letter, .b-letter__fake, .b-transaction-metadata, .b-letter__body, .b-letter_first .b-letter__head, .b-letter_first .b-letter__fake__head {
    z-index: 1;
    position: relative;
    top: -40px;
}


.b-letter_expanded.b-letter_thread {
    top: 0px;
}

.b-letter_expanded.b-letter_thread {
    padding-bottom: 40px;
    padding-top: 40px;
    top: -40px;
}

.compose, .b-kav-protected{
    z-index: 1;
}
}
