css:
@-moz-document url-prefix("https://www.baidu.com/s?"), url-prefix("https://www.baidu.com") {
/* 搜索标题 */
.s_form_wrapper {
		margin-left: 20%;	    
}
/* 导航栏 */
.s_tab_inner{
    margin-left: 21.3%;
}
/* 中间搜索结果整体左侧移动 */
#container{
	margin-left: 20% !important; 
}
/* 去掉侧边新闻 */
.FYB_RD {
		display: none;	    
}

@media only screen and (max-width: 1500px) {
    .s_form_wrapper {
        margin-left: 17.2%;
    }
    .s_tab_inner{
        margin-left:19.3%;
    }
    #container {
		margin-left: 0%;	    
	}
}






}
