css:
@-moz-document domain("subscene.com") {
body {
    background-image: none;
    background-color: #111;
    color: #aaa;
    border-bottom: 130px solid #111
}

body::-webkit-scrollbar {
    width: 11px;
}

body::-webkit-scrollbar-track {
    background: #2a2d2f;
}

body::-webkit-scrollbar-thumb {
    background: #404448;
    border-radius: 5.5px;
}

body::-webkit-scrollbar-thumb:hover {
    background: #4b5155;
}

.poster {
    border: none
}


#user-navigation, #forum-navigation, #admin-navigation {
    background: #000;
}

.content {
    border-top: none
}

.byFilm .sort-by, .byFilm .rating-guide span.visited {
    background-color: #222;
    color: #ccc;
    border: 2px solid #222;
}

.subtitle .comment, .window, footer {
    background-color: #333;
    color: #ccc;
        box-shadow: none;
    border-top: none
}

.subtitles table {
    background-color: transparent;
}

.subtitle .comment:before {
    background-image: none;
}

#search .text {
    background-color: #111;
        border-color: #444
}

a, a:link, .bread a, .bread a:visited {
    color: #ccc;
}

a, a:hover:link {
    text-decoration: underline;
}

a:visited {
    color: #ccc;
}

.byTitle .search-result {
    background-color: transparent;
}

h1, h2, h3, .h2 {
    color: #eee;
}

.subtitles td.a1 a:hover {
    background-color: transparent;
    color: white;
}


tr, .subtitles td.a1 a:link {
    background-color: #111;
}


tr:hover .a1 .a2 .a3 .a4 .a5 .a6 {
    background-color: #333;
}

.subtitles td.a1 a:visited, .subtitles td.a1 .visited {
    background-color: inherit;
}

.subtitles td a {
    color: #ccc
}

}
