css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("rs.4chan.org"){
* {font-family:"Trebuchet MS" !important; font-size:8pt !important; font-weight:normal !important; color:#EEE !important}
body {background:#000 !important}
a:hover {color:#123123 !important}
hr, #filter-button, #option-button, #footer center, #navbotr, #navtopr, .logo, #ad, .rules, iframe[src="http://www.4chan.org/rotating.php"], iframe {display:none !important}
#navtop, #navbot {width:100% !important; padding:0px !important; margin:0px !important; text-align:center !important; top:0 !important; z-index:30}
form {margin:1em !important}
.inputtext, textarea {width:295px !important}
.postblock {border:1px solid #000 !important}
th {padding:.2em !important; font-size:small; width:200px !important}
.pages {background:#000 !important; border:none !important; -moz-border-radius:3px !important}}
@-moz-document domain("zip.4chan.org"), domain("orz.4chan.org"), domain("tmp.4chan.org"), domain("bin.4chan.org"), domain("img.4chan.org"), domain("cgi.4chan.org"){
* {font-family:"Trebuchet MS" !important; font-size:8pt !important; color:#EEE !important; font-weight:normal !important}
body {background:#000 !important}
a:hover {color:#76BB83 !important}
hr, #filter-button, #option-button, #ft, #hd, .deletebuttons, #footer center, #navbotr, #navtopr, img[src*="/dontblockthis"], .logo, #ad, a[href*="http://www.4chan.org/advertise/"], .rules, img[src*="http://content.4chan.org/tmp"], td[align*="right"], .doubledash, iframe, th[bgcolor*="#e04000"] {display:none !important}
#navtop, #navbot {width:100% !important; padding:0px !important; margin:0px !important; text-align:center !important; top:0 !important; z-index:30}
form {margin:1em !important}
.inputtext, textarea {width:295px !important}
.postblock {border:1px solid #000 !important; background:#000 !important}
.reply {padding:.2em !important; border:none !important; background:#101010 !important; -moz-border-radius:0px 7px 0px 7px !important; width:1280px !important}
.replyhl {padding:.2em !important; border:none !important; background:#222222 !important; width:1280px !important; -moz-border-radius:0px 7px 0px 7px !important}
th {padding:.2em !important; font-size:small; width:200px !important}
.box-outer, .box-inner {border:none !important}
.pages {border:none !important; background:#000 !important; -moz-border-radius:3px !important}
pre, textarea {font-size:inherit ! important}
.unkfunc {color:#76BB83 !important}
a[href*="mailto:sage"]:after, a[href*="mailto:SAGE"]:after {content:" (sage)" !important}}
