css:
@-moz-document domain(popflash.site) {

html, body {
    background: #333;
    color: #3c3d4f;
    margin: 0;
    font-family: 'Roboto', sans-serif;
}

#background-overlay {
    position: absolute;
    top: 85px;
    left: 0;
    width: 100%;
    min-height: 300px;
    background: #333;
    -webkit-box-shadow: 0 1px 0 0 #e2e2e2, inset 0 1px 0 0 rgba(1,1,1,.05);
    box-shadow: 0 1px 0 0 #e2e2e2, inset 0 1px 0 0 rgba(1,1,1,.05);
    /* background: url(/pattern.png); */
    border-top: 1px solid #e7e7e7;
}

.content-box {
    border: 1px solid #E2E2E2;
    padding: 30px 35px;
    background: #333;
    min-height: 205px;
    font-size: 14px;
}

#page-container {
    border: 1px solid #737373;
    width: 960px;
    margin: 40px auto;
    min-height: 275px;
    position: relative;
    z-index: 1;
    background: #14171A;
    padding: 30px;
}

h2 {
    font-family: 'Roboto Condensed', sans-serif;
    color: #e83c2f;
    margin: 0 0 30px 0;
    padding: 5px;
    border-bottom: 1px solid #737373;
}

header {
    background: #737373;
    min-height: 85px;
}

header .logo {
    padding-left: 20px;
    padding-right: 50px;
    border-right: 1px solid #737373;
    float: left;
    height: 85px;
    line-height: 85px;
    background: #737373;
}

header .navigation {
    padding-left: 50px;
    float: left;
    background: #737373;
}

header .profile .overlay-container {
    display: none;
    position: absolute;
    top: 60px;
    right: 0;
    width: 165px;
    height: 200px;
    background: #333;
    border: 1px solid #E3E3E3;
    z-index: 99;
    padding: 15px;
    -webkit-box-shadow: 1px 1px 1px 0px rgba(0,0,0,.1);
    box-shadow: 1px 1px 1px 0px rgba(0,0,0,.1);
}

#header-container {
    width: 980px;
    margin: 0 auto;
    position: relative;
    font-family: 'Roboto Condensed', sans-serif;
    background: #737373;
}

html, body {
    background: #333;
    color: #cecece;
    margin: 0;
    font-family: 'Roboto', sans-serif;
}

/* unvisited link */
a:link {
  color: red;
}

/* visited link */
a:visited {
  color: green;
}

/* mouse over link */
a:hover {
  color: hotpink;
}

/* selected link */
a:active {
  color: blue;
}

}
