css:
@-moz-document domain("woodwind.org") {
a:-webkit-any-link {
    color: #a00;
}
table[bgcolor="#FFFFFF"], td[bgcolor="#FFFFFF"] {
    background-color: #000;
}
body {
    background-color: #000;
    color: #69b;
}
body[background="../../Images/p12c01.gif"] {
    background-image: none;
}
.adBox, .adBox:link, .adBox:visited, .adBox:active {
    background-color: #321;
    color: #fff;
}
.adTitle, font[color="#000000"] {
    color: #fff;
}
.forSale, .forSale:link, .forSale:visited, .forSale:active {
    background-color: #282;
    color: #fff;
}
font[color="#004040"] {
    color: #bff;
}
td[bgcolor="#FFFFEE"], tr[bgcolor="#FFFFEE"] {
    background-color: #110;
}
tr[bgcolor="#CCCCCC"], th[bgcolor="#CCCCCC"] {
    background-color: #222;
}
tr[bgcolor="#6699CC"], td[bgcolor="#6699CC"] {
    background-color: #037;
}
td[bgcolor="#e0e0e0"] {
    background-color: #111;
}
img {
    filter: invert(100%);
}
img[src="../images/title.jpg"], img[src="../images/nav_links.jpg"], img[src="../images/nav_guest.jpg"], img[src="../images/nav_forum.jpg"], img[src="../images/nav_fing.jpg"], img[src="../images/nav_home.jpg"], img[src="/images/help.jpg"], img[src="/images/title.jpg"], img[src="images/nav_links.jpg"], img[src="images/nav_guest.jpg"], img[src="images/nav_forum.jpg"], img[src="images/nav_fing.jpg"], img[src="images/nav_home.jpg"], img[src="images/help.jpg"], img[src="images/title.jpg"], .PhorumListTable tbody tr td img {
    filter: none;
}
img[src="../n/sharp_blue.gif"], img[src="../n/flat_blue.gif"] {
    filter: contrast(30%);
}
}
