css:
@-moz-document domain('youtube.com') {
    /** Remove Menu Item Left Bar "Trending" */
    #items > ytd-guide-entry-renderer:nth-child(2){
        display: none;   
    }

    /** Remove Menu Item Left Bar "Home" */
    #items > ytd-guide-entry-renderer:nth-child(1) {
        display: none;   
    }

    /** Remove comments on a video */
    #comments {
        display: none;   
    }

    /** Remove "Watch Next" section on a video */
    #related > ytd-watch-next-secondary-results-renderer {
        display: none;   
    }
}

/**
 * This section is applied only when the url is matching the main page /
 */
@-moz-document url('https://www.youtube.com/') {
    /** Remove Home section content **/
    #contents {
        display: none;
    }
}
