css:
@-moz-document domain("textivate.com") {
td, div#answertilediv {
    color: #ddd !important;
}
a.indexlink, a.indexlink:hover, .indexlinkltbg, a, b {
    color: #ddd;
}
body {
    color: #ddd;
    background-image: url(https://www.drupal.org/files/issues/background_5.png);
}
.bodybackground {
    background-image: url(https://www.drupal.org/files/issues/background_5.png);
}
.laddercell, .memblock {
    background-image: url(https://www.drupal.org/files/issues/background_5.png);
    border: none;
}
.col, td[class="paracolmulti lightbg"], div[style="background-color: rgb(255, 255, 255);"], div.lsoptionsdiv, div#aboutscrolldiv {
    background-color: #000 !important;
}
.col[style="border-color: white;"] {
    border-color: 222 !important;
}
div.toprightdiv, div#fbdiv, div#helpinfodiv, div.whitebox, .ivline, div.nearlyfullpage {
    background-color: #000;
}
textarea.bigtextnew {
    background-color: #000;
    color: #ddd;
}
.paracolmulti, .wbletter {
    background-color: #000;
    border: 1px solid #000;
}
.searchtablerow {
    background-color: #000;
    border: 1px dotted #333;
}
.paracol, .onecol, .dragablecell, .swcol {
    background-color: #000;
    border: 3px solid #000;
}
div.tacontdiv {
    background-color: #000;
    color: #ddd;
    border-top: 1px solid #000;
    border-left: 1px solid #333;
    border-right: 1px solid #333;
    border-bottom: 1px solid #333;
}
td.taboff {
    background-color: #000;
    border-top: 1px solid #333;
    border-left: 1px solid #333;
    border-right: 1px solid #000;
    border-bottom: 1px solid #333;
}
td.tabon {
    background-color: #000;
    border-top: 1px solid #333;
    border-left: 1px solid #333;
    border-bottom: 1px solid #000;
}
td.taboff:hover {
    background-color: #111;
}
td.bottombar, td.bottombar:hover {
    background-color: #111 !important;
}
td.taemptytab {
    border-left: 1px solid #333;
    border-bottom: 1px solid #333;
}
div[style="width:60px;height:16px;background:#ffffff"], div[style="width:60px;height:5px;background:#ffffff"] {
    background: #000 !important;
}
.snake {
    background: #000;
}
div[style="padding:0px;text-align:left;background-color:#eeeeee;border-radius: 0px 0px 4px 4px; -moz-border-radius: 0px 0px 4px 4px; -webkit-border-radius: 0px 0px 4px 4px; border-left:1px solid #cccccc;border-right:1px solid #cccccc;border-bottom:1px solid #cccccc;"] {
    background-color: #111 !important;
    border-left: 1px solid #333 !important;
    border-right: 1px solid #333 !important;
    border-bottom: 1px solid #333 !important;
}
.lightbg {
    background-color: #222;
}
div[style="display:block;width:90%;height:90%;border:1px #cccccc solid;background-color: #ffffff;padding:10px;overflow:auto;-webkit-overflow-scrolling: touch;text-align:justify;font-size:14px;"], div[style="background:#f6f6f6;border:1px solid #cccccc; padding:20px;"] {
    border: 1px #cccccc solid !important;
    background-color: #000 !important;
}
.col[id="tile1"], .col[id="tile2"], .col[id="tile3"], .col[id="tile4"], .col[id="tile5"], .col[id="tile6"], .col[id="tile7"], .col[id="tile8"], .col[id="tile9"], .col[id="tile10"], .col[id="tile11"], .col[id="tile12"], .col[id="tile13"], .col[id="tile14"], .col[id="tile15"], .col[id="tile16"] {
    border-color: #000;
}
.clickablecell {
    border: 1px solid #000;
    background-color: #000 !important;
}
/*AEFCFE*/
.aasbuttonfour:hover {
    border: 1px solid #266;
    background-color: #000;
}
.aasbuttonfour {
    color: #ddd;
    border: 1px solid #022; /*76C6C9*/
    background-color: #000;
}
}
