css:
@-moz-document url("http://www.animeplus.tv"), domain("mangakakalot.com"), domain("manganelo.com") {
iframe,
body > a[href],
.mb10,
.tips > div[style],
.c,
#shares,
#advertisment,
#rss,
#footer,
.advertisment,
/* #spotlights, */
#intro > span,
.left > div:nth-child(4),
#mfad600x90,
#ad_top,
.ad,
#ad,
#intro,
#top.topless,
.fb-page.fb_iframe_widget,
.stcpDiv,
#fb-root,
.tab_comment.clearfix li:first-child,
.fb-page,
[id^=ad],
[class^=ad],
[id^=fb],
[id^=Market],
[id^=M301863],
.footer-content {
    display: none!important;
}

/* 
*/
li span a,
.sts {
    color: wheat !important;
}
a,
p,
.xem-nhieu-item h3 a,
h3 a {
    color: silver   !important;
}
li span a:visited,
span a:visited,
.doreamon a:visited {
    color: crimson !important;
}
a:hover {
    color: yellow;
}
a:active {
    color: #fff !important;
}

/* BACKGROUND */

/* .trang-doc {
	background:#1f1f1f!important;
} */

/* LAYOUT */
.top-logo a img {
    border: 3px solid #1B1B1D;
    border-radius: 5px;
    padding: 4px;
    border-top-left-radius: 20px;
    background-image: -moz-linear-gradient(bottom left, #3f3f48 15%, #3f3f48 40%);
}

/* .top-logo a img:hover,  */
.menu-item:hover {
    /*     box-shadow: 0 0 2px 1px #FF6!important; */
    text-shadow: 0 0 5px rgba(108, 210, 248, 0.7), 0 0 3px rgba(255, 255, 255, 1), 0 0 1px rgba(0, 0, 0, 1);
    color: #666666!important;
    /*     box-shadow: 0 0px 4px 4px rgb(255, 255, 255),0 1px 4px 4px rgba(0, 0, 0, .48) */
    transition: all 0.4s linear;
    top: -3px;
    position: relative;
}
.menu-item li a:hover {
    text-shadow: 0 8px 6px -6px black;
}
.itemupdate.first {
    box-shadow: 0 8px 16px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19);
    padding-right: 1.00%;
}
a img {
    background-blend-mode: darken!important;
}
.top-header {
    /*     border-right: 1px white solid;
    border-left: 1px white solid;
    border-top: 1px white solid; */
    width: 78%;
    border-top-right-radius: 20px;
}
#menu-menu-top {
    /*     border-right: 1px white solid;
    border-left: 1px white solid;
    border-bottom: 1px white solid; */
    width: 80%;
    padding-bottom: 8px;
}

/* BACKGROUND */

/* .top-header{
	background: url(http://read.lhtranslation.com/app/manga/themes/default/assets/images/background.jpg) repeat, white;
	-webkit-text-fill-color: transparent;
	-webkit-animation-name: masked-animation;
	-webkit-animation-duration: 40s;
	-webkit-animation-iteration-count: infinite;
	-webkit-animation-timing-function: linear;
} */
@-webkit-keyframes masked-animation {
    0% {
        background-position: left bottom;
    }
    100% {
        background-position: right bottom;
    }
}
body,
footer,
#chapter,
.comments,
.new_update_trangdoc,
.option_wrap {
    background: #1B1B1D;
    background-color: #1B1B1D;
    border-top: unset!important;
}
.top-header,
.list-truyen-item-wrap,
.spec-tag,
.spec-tag li,
.spec-tag li a,
.daily-update-item {
    background: #3F3F48!important;
    background-color: #3F3F48!important;
}
#menu-menu-top,
.manga-info-chapter,
#chapter,
.page,
.tags,
.tag,
.tag a,
.tag li,
.tag.tag-left,
.xem-them:hover {
    background: #2A2A2E;
    background-color: #2A2A2E;
    border-top: unset;
}
.doreamon {
    border-top-right-radius: 10px!important;
    background: #3F3F48!important;
    background-color: #3F3F48!important;
    background-blend-mode: darken!important;
    background-position: top !important;
    background-repeat: no-repeat!important;
}
.tag-title,
.title.all-title,
.title.update-slide {
    background: #00000080 !important;
    border-spacing: 0!important;
    background-attachment: fixed !important;
    background-position: 50% 0% !important;
    border-top: unset!important;
}
.itemupdate:nth-child(2n+1) {
    background-color: #1E1E20 !important;
}

/* .xem-nhieu-item:nth-of-type(even) {
    background-color: #00000080 !important;    
    background-attachment: fixed !important;
}
.xem-nhieu-item:nth-of-type(odd) {
    background-color: #3F3F48 !important;
    background-attachment: fixed !important;
    background-position: 50% 0!important;
} */
.daily-update,
.update-title,
.xem-nhieu,
.slide {
    background-color: #00000040 !important;
    border: unset;
}
.row1,
.post_block.row1 {
    background-color: #00000040 !important;
}
.rowitemupdate {
    display: block!important;
}
.bookmark_list .bookmark_item, .history_list .history_item {
    padding: 10px 0;
    width: 100%;
    float: left;
    background: #1E1E20;
    border-left: 2px solid #31a95b;
    font: bold 12px/30px Tahoma,Geneva,sans-serif;
    color: #31a95b;
    margin-bottom: 5px;
}
.user-panel-left {
    float: left;
    width: 800px;
    background-color: #1E1E20;
    margin-right: 10px;
    overflow: hidden;
    font: 400 14px Open Sans,Tahoma,Geneva,sans-serif;
    color: #808080;
    box-sizing: border-box;
    padding: 10px;
    min-height: 250px;
}
.user-panel-right .user-panel-setting {
    box-sizing: border-box;
    float: left;
    width: 100%;
    background: #1E1E20;
    padding: 10px;
    border-top: 2px solid #31a95b;
}

}
