@-moz-document domain("allmystery.de") {
/* Site Settings */

* {
	letter-spacing: 2px!important;
	text-decoration: none!important;
	transition: none!important;
}

#tabnav-all-opener,

a.tab
{
	text-transform: capitalize!important;
}

.avatar:after,
.nice-frame:after,
.start .areas .area .last,
.start .areas .area .main,
.threads .threads-list .last-post,
.threads .threads-list .posts-count,
.threads .threads-list .thread-container,
.threads .threads-list .threads-header,
.threads .threads-list .threads-header-centered,

:focus
{
	border: none!important;
	box-shadow: none!important;
	outline: none!important;
    text-shadow: none!important;
}

.desktop .threads .threads-list .last-post,
.desktop .threads .threads-list .posts-count,
.desktop .threads .threads-list .thread,
.desktop .threads .threads-list .thread-container,
.desktop .threads .threads-list .thread-remove,
.desktop .threads .threads-list .threads-header,
.desktop .threads .threads-list .threads-header-centered,
.start .areas .area,
.start .areas .area .last,
.start .areas .area .main
{
	display: inline-block;
}

.videos .videoitem .detailinfo {
    float: right;
}

/* Fonts 
_____________________________________________________________________________________________________*/

.PostHead,
.threads .threads-list .thread-container .thread-title,

h3
{
	font-size: 25px!important;
}

body {
	font-size: 20px!important;
}

#tabnav-all-opener,

.nu,

a.tab,
blockquote cite
{
	font-size: 19px!important;
}

.header,
.mcell_inhalt,

small
{
	font-size: 15px!important;
}

.post,
.start .areas .area .last,
.start .areas .area .main,
.start .areas .area .smain,
.threads .threads-list .pageselector,
.threads .threads-list .thread,

h3
{
	line-height: 24px!important;
}

.mcell_inhalt
{
	line-height: 19px!important;
}

/* Remove Items 
_____________________________________________________________________________________________________*/

#rekt,

.adSkyscraperHolder,
.adfbeitrag .adsbygoogle
{
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/* Left/Right/Top/Bottom 
_____________________________________________________________________________________________________*/

/* Margin-Top ___________________________*/

.start .areas .area .last {
	margin-top: -7%;
}

/* Margin-Left ___________________________*/

.start .areas .area .last {
	margin-left: 66%;
}

/* Margin-Right ___________________________*/

.main {
	margin-right: 0;
}

/* Padding-Right ___________________________*/

html.desktop.am {
	padding-right: 0;
}

/* Left ___________________________*/

.threads .threads-list .thread .icon-thread, .threads .threads-list .pageselector .icon-thread {
    left: 6px;
}

/* Right ___________________________*/

.infoleiste {
	right: 10px;
}

/* Margins 
_____________________________________________________________________________________________________*/

.infoleiste,
.post,
.main-holder,
.nice-doc,
.videos .videoitem
{
	margin: 1.2rem!important;
}

blockquote {
    margin: 0 0 37px;
}

.videos .videoitem .text,
.videos .videoitem h3 {
	margin: 3px 6rem!important;
}

.desktop .threads .threads-list .thread,
.start .areas .area:nth-of-type(odd),
.start .areas .area {
	margin: 3px 12px!important;
}

/* Paddings 
_____________________________________________________________________________________________________*/

.desktop .threads .threads-list .thread,
.main-holder,
.post,
.start .areas .area,

blockquote
{
	padding: 1.2rem!important;
}

.start .areas .area .smain,
.start .areas .area .last
{
    padding: 20px 93px;
}

/* Heights 
_____________________________________________________________________________________________________*/

.start .areas .area .smain,
.start .areas .area .last
{
    height: auto;
}

.start .areas .area {
	height: 7rem!important;
}

.desktop .threads .threads-list .thread {
	height: 4rem!important;
}

/* Widths 
_____________________________________________________________________________________________________*/

body {
	max-width: none!important;
}

.start .areas .area {
	width: 100%!important;
}

.desktop .threads .threads-list .thread,
.main-holder {
	width: 98%!important;
}

.cattags,
.main {
	width: 75%;
}

.threads .threads-list .thread-container,
.start .areas .area .main {
	width: 60%!important;
}

.threads .threads-list .last-post,
.start .areas .area .last {
	width: 26%!important;
}

.infoleiste {
	width: 18%;
}

.finfo, .adposthint {
    width: 11%;
}

}

@-moz-document domain("allmystery.de") {
/* Font Settings */

a[href]
{
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited
{
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

/* Yellow 
_____________________________________________________________________________________________________*/

.last-post .thread-details a font,
.thread-container .thread-details a:first-child font,

a[href]:hover,
h1,
input[type="checkbox"]:checked+label,
input[type="radio"]:checked+label
 {
	color: #ff0!important;
}

/* White 
_____________________________________________________________________________________________________*/

.start .areas .area .smain .count,
.start .areas .area .main .count,
.threads .threads-list .thread-details,

blockquote,
body,
li,
p
 {
	color: #fff!important;
}

/* Khaki 
_____________________________________________________________________________________________________*/

.threads-list .threads-header,

h1,
h2
 {
	color: #F0E890!important;
}

/* Burlywood 
_____________________________________________________________________________________________________*/

#se,

.tabnav-all,

h3,
h4
 {
	color: #e0b880!important;
}

/* Sandy Brown 
_____________________________________________________________________________________________________*/

h5,
h6,
label
 {
	color: #F0A060!important;
}

/* Blue 
_____________________________________________________________________________________________________*/

.fbeitrag .date,
.start .areas .area .last a.lasttime,
.threads-list .thread-details font,
.videos .videoitem .detailinfo,

a[title="to the last post"] font,
time
 {
	color: #07beff!important;
}

/* Green 
_____________________________________________________________________________________________________*/

selector
 {
	color: #07ff24!important;
}

/* Red 
_____________________________________________________________________________________________________*/

selector
 {
	color: #ff0707!important;
}

/*
_____________________________________________________________________________________________________*/

input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea
{
	background-color: #5d1b1b!important;
	color: #fff!important;
}

/*
_____________________________________________________________________________________________________*/

::selection
{
	background: #ff9!important;
	color: #000!important;
}

pre ::selection
{
	background: #ff9!important;
	color: #fff!important;
}

::placeholder
{
	color: #f48f8f!important;
	opacity: 1;
}

}

@-moz-document domain("allmystery.de") {
/* Background Settings */

body,
html
{
	background: #000!important;
}

.header,
.mcell_inhalt input[type='password'] {
	background-image: none!important;
}

/* Background 
_____________________________________________________________________________________________________*/

.header
{
	background: #060505!important;
	background-color: #060505!important;
}

#se,

.editor .editor-controls,
.editor .editor-file-uploader,
.tabnav,
.tabnav-all
{
    background-color: #101010!important;
}

/* Background Transparent 
_____________________________________________________________________________________________________*/

#tabnav-all-opener,

.Password-Holder a,
.btn,
.mcell,
.mcell_inhalt,
.nice-footer,
.paginator,
.post,
.start .areas .area,
.start .areas .area:nth-of-type(odd),
.start .subselector,
.threads .threads-list .pageselector:nth-of-type(even),
.threads .threads-list .thread:nth-of-type(even),
.threads-list .pageselector:nth-of-type(even),
.threads-list .pageselector:nth-of-type(odd),
.threads-list .thread:nth-of-type(even),
.threads-list .thread:nth-of-type(odd),

a.tab
{
	background: #0000!important;
	background-color: #0000!important;
}

/* Buttons 
_____________________________________________________________________________________________________*/

/* Blue 
_____________________________________________________________________________________________________*/

.btn:not(.icon-login)
{	
	background: radial-gradient(circle, #0f79af 0%, #031017 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #fff!important;
	opacity: 1!important;
	padding: .5rem!important;
	position: relative!important;
	z-index: 1!important;
}

.btn:not(.icon-login)::before
{
	background: radial-gradient(circle, #031017 0%, #0f79af 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .8s linear!important;
	z-index: -1!important;
}

.btn:not(.icon-login):hover:before
 {
  opacity: 1!important;
}

/* Plain Black 
_____________________________________________________________________________________________________*/

.nice-doc
{
	background-color: #000!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px #0006, #373737 0 1px 0, 10px 10px 5px #0006, #9b9b9b 0 1px 0!important;
	padding: .5rem!important;
}

/* Plain 
_____________________________________________________________________________________________________*/

.desktop .threads .threads-list .thread,
.in,
.infoleiste,
.post,
.start .areas .area,
.start .areas .area:nth-of-type(odd),
.threads .threads-list .pageselector,
.threads .threads-list .thread,
.videos .videoitem
{
	background-color: #111!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px #0006, #373737 0 1px 0, 10px 10px 5px #0006, #9b9b9b 0 1px 0!important;
	padding: 1.5rem!important;
}

.desktop .threads .threads-list .thread:hover,
.in:hover,
.infoleiste:hover,
.nice-doc:hover,
.post:hover,
.start .areas .area:hover,
.start .areas .area:nth-of-type(odd):hover,
.videos .videoitem:hover
{
	box-shadow: inset 0 0 5px #0006, #9b9b9b 0 1px 0, 10px 10px 5px #0a0a0a66, #9b9b9b 0 1px 0!important;
}

/* Red Gradient 
_____________________________________________________________________________________________________*/

.header_info_warning
{
	background: linear-gradient(to bottom, #300202 0%, #590505 100%)!important;
}

/* Highlight Background 
_____________________________________________________________________________________________________*/

.threads-list .thread:nth-of-type(even):hover,
.threads-list .thread:nth-of-type(odd):hover,
.unread-bookmarks a:hover
{
	background: linear-gradient(to right, #3b0202, #0000)!important;
	border-left-color: #434343!important;
}



}

@-moz-document domain("allmystery.de") {
/* Image Settings */

.desktop .threads .threads-list .thread,
.icon-up:before,
.post,
.start .areas .area,
.unread-bookmarks a,.threads-list .thread:nth-of-type(even),
.threads-list .thread:nth-of-type(odd),
.videos .videoitem
 {
	filter: brightness(75%)!important;
}

/*
_____________________________________________________________________________________________________*/

.desktop .threads .threads-list .thread:hover,
.icon-up:hover:before,
.post:hover,
.start .areas .area:hover,
.unread-bookmarks a:hover:hover,
.threads-list .thread:nth-of-type(even):hover,
.threads-list .thread:nth-of-type(odd):hover,
.videos .videoitem:hover
 {
	filter: brightness(100%)!important;
}

/*
_____________________________________________________________________________________________________*/

img
 {
	border-radius: 22px!important;
}

/*
_____________________________________________________________________________________________________*/

img
 {
	-webkit-box-reflect: below 0 -webkit-gradient(linear, left top, left bottom, from(transparent), color-stop(70%, transparent), to(rgba(250, 250, 250, 0.1)));
}

}

@-moz-document domain("allmystery.de") {
/* New Icons */

input[type="checkbox"],
input[type="radio"] {
    appearance: none!important;
}

.icon-thread:before,
.icon-help:before {
	content: ''!important;
}

/* Arrows _______________________________________________________________________*/

.icon-up:before {
    content: '';
}

/* Up _________________*/

.icon-up:before
{
	background: url(https://i.ibb.co/bvZmSFj/red-up.png) no-repeat!important;
	box-sizing: border-box;
}

/* Down _________________*/

selector
{
	background: url(https://i.ibb.co/z7MRv74/direction-arrow-down.png) no-repeat!important;
	box-sizing: border-box;
}

/* Left _________________*/

selector
{
	background: url(https://i.ibb.co/mRKFvH8/direction-arrow-left.png) no-repeat!important;
	box-sizing: border-box;
}

/* Right _________________*/

selector
{
	background: url(https://i.ibb.co/LQDKzQT/direction-arrow-right.png) no-repeat!important;
	box-sizing: border-box;
}

.icon-up:before
{
    background-size: 35px!important;
    height: 36px!important;
    padding-left: 36px!important;
    width: 10px!important;
}

/* Comment _________________________________________________________________________________*/


[href="/ng/random"]:after {
    content: "";
}

[href="/ng/random"]:after
{
	background: url(https://i.ibb.co/98nZdCB/comment1.png) no-repeat!important;
	box-sizing: border-box;
}

[href="/ng/random"]:after
 {
    background-size: 25px!important;
    height: 26px!important;
    padding-left: 26px!important;
    width: 1px!important;
}

/* Help _______________________________________________________________*/

.icon-help:before {
	background: url(https://i.ibb.co/52jLWbb/avatar-l.png) no-repeat!important;
}

.icon-help:before {
	background-size: 10px!important;
	height: 31px!important;
	width: 31px!important;
}

/* Lock ______________________________________________________________________________*/

.thread-closed.icon-thread:after {
    content: '';
}

.thread-closed.icon-thread:after
{
	background: url(https://i.ibb.co/PDvx7bz/lock124x124.png) no-repeat!important;
	box-sizing: border-box;
}

.thread-closed.icon-thread:after
{
    background-size: 25px!important;
    height: 26px!important;
    padding-left: 26px!important;
    width: 10px!important;
}

/* LogIn ___________________________________________________________________________*/

.icon-login:before {
    content: '';
}

.icon-login:before
{
	background: url(https://i.ibb.co/fpGB5C8/login.png) no-repeat!important;
	box-sizing: border-box;
}

.icon-login:before
{
	background-size: 35px!important;
	height: 36px!important;
	padding-left: 36px!important;
	width: 1px!important;
}

/* Play ______________________________________________________________________________*/

.icon-volume:before {
    content: "";
}

.icon-volume:before
{
	background: url(https://i.ibb.co/7VB6KK3/red-play.png) no-repeat!important;
	box-sizing: border-box;
}

.icon-volume:hover:before
{
	background: url(https://i.ibb.co/qdS3kwB/red-play-hover.png) no-repeat!important;
	box-sizing: border-box;
}

.icon-volume:hover:before,
.icon-volume:before
{
	background-size: 35px!important;
	height: 36px!important;
	padding-left: 36px!important;
	width: 100%!important;
}

}

@-moz-document domain("allmystery.de") {
/* Scrollbar Settings */

/* Width 
_____________________________________________________________________________________________________*/


 ::-webkit-scrollbar {

 	width: 1.2em!important;
 }

 /* Color of handle 
__________________________________________________________________________________________________________________________________*/


 ::-webkit-scrollbar-thumb {
 	background: #580303!important;
 	background-image: linear-gradient(to right, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border-radius: 22px!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 ::-webkit-scrollbar-thumb:hover {
 	background: #580303!important;
 	background-image: linear-gradient(to left, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of Track 
_____________________________________________________________________________________________________*/



 ::-webkit-scrollbar-track {
 	background: #170000!important;
 	box-shadow: inset 8px 0 13px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 /* Color of handles at top and bottom 
_____________________________________________________________________________________________________*/

 ::-webkit-scrollbar-button {
 	background: #4c0707!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

 ::-webkit-scrollbar-button:hover {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of the bottom corner of the scrollable element, where two scrollbar meet. 
_____________________________________________________________________________________________________*/

::-webkit-scrollbar-corner
 {
 	background: #530205!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

::-webkit-scrollbar-corner:hover
 {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

}
