css:
@-moz-document url-prefix("https://forums.automobile-propre.com") {
:root {
  --main-bg-color: #252525;
  --main-txt-color: #f8f8f2;
  --subtext-color: #a1a19b;
  --menu-txt: #999;
  --light-border: #3b3b3b;
  --box-color: rgba(255, 255, 255, 15%);
  --forum-list-even: rgba(255, 255, 255, 4%);
  --section-title: rgba(0, 0, 0, 25%);
  --unread: #2196F3;
}

body,
.g-bg-white,
.ipsAlert,
.ipsMenu,
.ipsTabs_activeItem,
.ipsTabs_panel,
.ipsComment_content,
.ipsHovercard,
#ipsLayout_contentWrapper,
.ipsTooltip a {
  background: var(--main-bg-color) !important;
  color: var(--main-txt-color) !important;
}

.g-color-black,
.ipsType_pageTitle,
.ipsType_sectionHead {
  color: var(--main-txt-color) !important;
}

.ipsBreadcrumb > ul > li,
.ipsBreadcrumb [data-action="defaultStream"],
.ipsBreadcrumb [data-action="markSiteRead"],
.ipsType_richText,
.ipsType_break a,
.ipsMenu_item a,
.ipsType_light {
  color: var(--subtext-color) !important;
}

#ipsLayout_header header,
#ipsLayout_header nav.ipsNavBar_noSubBars:after,
.ipsButtonBar,
.ipsBox:not( .ipsBox_transparent ):not( .ipsModerated ),
.ipsNavBar_primary > ul > li > a,
.ipsNavBar_primary.ipsNavBar_noSubBars > ul > li.ipsNavBar_active > a,
.ipsSideMenu_list,
.ipsDataItem {
  background-color: transparent !important;
}

.ipsReactOverview ul img {
  border-color: transparent !important;
}

#elUserNav > li > a,
#elFooterSocialLinks > li > a,
.ipsNavBar_primary > ul > li > a,
.ipsDataItem_title a,
#elSearchWrapper .cSearchSubmit{
  color: var(--menu-txt);
}

.u-header{
  box-shadow: 0 5px 10px -6px var(--box-color);
  position: absolute;
}

.g-brd-bottom-1,
.g-brd-top-1,
.ipsBox,
.ipsApp .ipsButton_link.ipsFollow,
.ipsApp .ipsButton_link.ipsPromote,
.ipsQuote,
.ipsItemControls,
.ipsHr,
.ipsMenu,
.ipsMenu_sep,
#elSearchExpanded,
.ipsDataItem,
.ipsMenu_headerBar,
.ipsMenu_footerBar,
.ipsComment_header,
.ipsTooltip a {
  border-color: var(--light-border) !important;
}

.ipsDataItem_unread{
  border-left: 3px solid var(--unread) !important;
}

.ipsAreaBackground_reset{
  background: var(--main-bg-color) !important;
}

.ipsAreaBackground,
.ipsDataList.ipsDataList_zebra .ipsDataItem:nth-child(even),
.ipsMenu_item a:hover,
.ipsSideMenu_item:hover {
  background: var(--forum-list-even) !important;
}

.ipsType_sectionTitle,
.ipsButtonBar,
.ipsMenu_title,
.ipsWidget .ipsWidget_title,
.ipsMenu_headerBar, .ipsMenu_footerBar,
.ipsComment_header {
  background: var(--section-title) !important;
}

.ipsPagination a,
.ipsButtonBar .ipsButtonRow li > a,
.ipsButtonBar .ipsButtonRow li > button,
.ipsButtonBar .ipsButtonRow li > span,
.ipsApp .ipsButton_link.ipsFollow,
.ipsApp .ipsButton_link.ipsPromote,
.ipsQuote,
.ipsQuote .ipsQuote_citation a,
.ipsDataItem_meta.ipsContained > p > span,
.ipsPageHeader .ipsType_richText > p > span {
  color: var(--subtext-color) !important;
  background: transparent !important;
}

.ipsPagination .ipsPagination_active a,
.ipsReact_unreact,
.ipsAreaBackground_light,
#elSearchExpanded,
.ipsTooltip {
  background: var(--light-border) !important;
}

.ipsFollow .ipsCommentCount,
.ipsQuote_citation,
.ipsStream .ipsStreamItem_contentType {
  color: var(--subtext-color) !important;
  background: var(--light-border) !important;
}

html[dir="ltr"] .ipsFollow .ipsCommentCount:after,
html[dir="ltr"] .ipsCommentCount:after {
  border-color: transparent var(--light-border) transparent transparent;
}

.ipsMenu.ipsMenu_bottomCenter:before,
.ipsMenu.ipsMenu_bottomCenter:after,
.ipsTooltip.ipsTooltip_bottom:after {
  border-color: transparent transparent var(--light-border) transparent;
}

.ipsItemStatus:not( .ipsItemStatus_large ){
  color: var(--unread);
}

.ipsItemStatus:not(.ipsItemStatus_read).ipsItemStatus_large,
.ipsItemStatus:not(.ipsItemStatus_read).ipsItemStatus_custom{
  border-bottom: 5px solid var(--unread);
}

.ipsItemStatus.ipsItemStatus_custom{
  background: #fff;
  border-radius: 10px;
}

#ipsEmbedLoading{
  display: none !important;
}

label{
  margin-bottom: 0 !important;
}

#elSearchWrapper.cSearchExpanded {
  padding-bottom: 8px;
  padding-top: 0;
  margin-top: 0;
}
}
