css:
@-moz-document url-prefix("https://hardverapro.hu") {
#header-sticky {background: #121212 !important;}
#header-sticky .nav-link:hover {color: #e54c09 !important;text-decoration: none !important;}

main {background: #121212!important; color:#ececec;}
.card {background: #282828 !important; color:#ececec;}
.card .list-group-item a {color: #ececec !important;}
.uad-list .media.featured {background: #282828 !important;}
.uad-list .media a {color:#ececec !important;}
.uad-list h3{background:#464643 !important;; color:#fff !important;; border-bottom: 1px solid rgba(0,0,0,.125);}
.uad-categories-item a {color: #ececec !important;}
.breadcrumb a {color:#ececec !important;;}
.card-forum .card-header { background-color: #eca701 !important; color:#fff}
#page-index>.container {background: #282828 !important;}
.msg-list .card .card-header { background-color:#464643 !important;; color:#c5c5c5 !important; border-bottom: 1px solid #595959; border-top: 1px solid #595959;}
.msg-list .card .card-body {background: #282828 !important; }
#foot { background: #121212 !important;; color:#ececec !important;}
.table>tbody>tr>td.cell-face a, .table>tbody>tr>th.cell-face a, .table>thead>tr>td.cell-face a, .table>thead>tr>th.cell-face a, .table>tfoot>tr>td.cell-face a, .table>tfoot>tr>th.cell-face a {color: #fff;}
#page-forum>.container {background: #2f2f2f !important;}
.uad-list .uad-light { color: #e3e3e3 !important;}
.uad .uad-actions {background-color: #464643 !important;}
.uad .uad-details {background-color: #464643 !important;}
.jumbotron {background-color: #464643 !important; color:#ececec}
.list-inline {color:#ececec !important;}
.uad #uad-images-carousel {background-color: #464643 !important;}
li.media {background-color: #464643 !important;}
.uad .uad-content-block{background: #282828 !important}
.uad .uad-content {background: #282828 !important}
.table>tbody>tr>td, .table>tbody>tr>th, .table>thead>tr>td, .table>thead>tr>th, .table>tfoot>tr>td, .table>tfoot>tr>th {color: #fff;}
.dropdown-menu.show {background: #282828; color:#fff}
.dropdown-menu .dropdown-item {color:#fff}
.content-list.content-list-small .media {background: #282828;}
.form-control-rtif {background: #282828 !important;; border: 1px solid #464643;}
.rtif-formats {background: #464643;; border: 1px solid #464643;}
.rtif-formats>.btn-group .btn {background: #282828;  border-color: #464643;}
.btn-light:hover, .btn-light:not(:disabled):not(.disabled):active, .btn-light:not(:disabled):not(.disabled).active {
    background-color: #585757 !important;;
    border-color: #464643 !important;}
p {color:#fff}
.modal-body {background: #282828; color:#fff}
.card-primary .card-body {
    background: #464643;}
#page-index #middle>.container {
    background: #333;}
.uad .uad-time-location {color: #e3e3e3;}
}

@-moz-document url-prefix("https://hardverapro.hu/hirdetesfeladas") {
table td{background: #282828;}
.table thead th{background: #282828;}
.btn-outline-light {color: #ececec;}
.table>tbody>tr>td, .table>tbody>tr>th, .table>thead>tr>td, .table>thead>tr>th, .table>tfoot>tr>td, .table>tfoot>tr>th{color:#ececec !important; background:#282828 !important}
.modal-body {background: #121212;}
.uad-bundles .uad-bundle {color:#0c0c0c;}
.rtif-formats>.btn-group .btn {background: #ffffff;}
.rtif-formats {color:#333;}
}

@-moz-document url-prefix("https://hardverapro.hu/privatok"), url-prefix("https://hardverapro.hu/privat") {
.btn:not(:disabled):not(.disabled) {color:#ececec !important;}
.thread-list .media-body {background: #333 !important;}
.thread-list .thread-list-header {background-color: #464643 !important;}
.pager-navbar .navbar-nav .btn, .pager-navbar .navbar-nav .btnt:hover, .pager-navbar .navbar-nav .btn:focus  {background-color: #464643 !important;}
.dropdown-menu .dropdown-item.active {background-color: #464643 !important;}
.thread-list a {color:#ececec !important;}
.thread-list .col {color:#ececec !important;}
b {color:#fff !important;}
#page-forum #top>.container {background: #2f2f2f !important}
#page-forum #middle>.container {
    background: #333;}
}
