css:
@-moz-document domain("docbase.io") {
/* Content(Center) */
.content__main,
.content__header,
.dashboard__header,
.content__header-stretch,
.js-sorter,
.post-diff {
    background-color: #222 !important;
    background-image: url(data:image/png;base64,) !important;
    background-clip: border-box !important;
    background-origin: padding-box !important;
    background-attachment: scroll !important;
    background-repeat: repeat !important;
    background-size: auto !important;
    background-position: left top !important;
}
.label-list-item__link {
    color: #db4545 !important;
}
.post-header__title,
h1,
h2 {
    color: #ffffffed !important;
}
.content__main p,
.content__main ul,
.content__main li,
.content__main div,
tr,
td {
    color: #fffffff5 !important;
}
.dashboard-list-footer__count-icon {
    margin-right: 4px;
    font-size: 21px;
    color: #f6f6f6;
    vertical-align: -2px;
}
tr,
td {
    color: #fffffff5 !important;
    background-color: #222 !important;
}
archive-checkbox__label {
    color: #666 !important;
    opacity: 1 !important;
}
.post-activities__post-content-assign {
    background: #4597b2;
}
.readme__title a {
    color: #ffffffdb !important;
    background-color: #222 !important;
}

/* Header */
.sorter__opener,
.archive-checkbox__label,
.js-search-form,
.js-current-field,
.js-field-container {
    color: #666 !important;
    background-color: #fff !important;
}

.sorter__list-item {
    background-color: #222 !important;
}

/* Left Menu  */
.main-menu,
.main-menu__header,
.teams-menu__current-team,
.my-menu {
    background-color: #1e2f40 !important;
    box-shadow: 2px 0px 0 rgba(0, 0, 0, 0.2) !important;
}
.my-menu-category-list__item-link,
.my-menu__text-username {
    color: #d6e3e8 !important;
}
.my-menu-category-list__item-badge {
    color: #222 !important;
}
.nav-list__item-link span,
.support__opener span {
    color: #d6e3e8 !important;
}
.nav-list__item.is-active > a {
    background-color: #1e4147 !important;
}
.main-menu__header-logo {
    fill: #ece9e9 !important;
}

/* Code diff */
.post-diff__line-number,
.post-diff__info,
.post-activities__post-content-main,
.post-activities__item.is-current {
    background-color: #222 !important;
}
.post-diff__line-list-item--inc {
    background: #002800 !important;
    border-color: #060 !important;
}
.post-diff__line-list-item--inc strong {
    background: #252 !important;
    color: #eee !important;
}
.post-diff__line-list-item--dec {
    background: #380000 !important;
    border-color: #600 !important;
}
.post-diff__line-list-item--dec strong {
    background: #622 !important;
    color: #eee !important;
}

.post-contents li>code, .post-contents li>p>code {
    background: #173f4c !important;
}
}
