css:
@-moz-document url(http://jojodiamondrecords.shoutwiki.com) {
body {
    background-image: url(https://i.imgur.com/A567QwS.png);
    background-repeat: no-repeat;
    background-color: #0d0b0e
}

h1,
h2,
h3,
h4,
h5,
h6 {
    color: #fff
}
.mw-body,
.parsoid-body {
    background-color: #0e0b0f;
    color: #b7b7b7
}
table.wikitable,
table.wikitable > tr > th,
table.wikitable > tr > td,
table.wikitable > * > tr > th,
table.wikitable > * > tr > td {
    background-color: #000
}
element, .mw-body h1, .mw-body-content h1, .mw-body-content h2, .mw-body-content h3, .mainheader {
    border: #000;
    color: #423232;
}
:root {
    --global-color: #613f71;
    --global-secondary-color: #281a2f;
    --global-tertiary-color: #c380db;
    --global-user-admin: #a1e44c;
}

table.wikitable, table.wikitable > tr > th, table.wikitable > tr > td, table.wikitable > * > tr > th, table.wikitable > * > tr > td {
    border: 1px solid #1b1b1b;
    background-color: #0e0b0f
}
table.statueslist tbody tr th, table.statueslist h3 {
    background: #281a2f;
}

table.statueslist td, table.statueslist th {
    color: #ccc;
}
.mw-body .firstHeading, element, .mw-body h1, .mw-body-content h1, .mw-body-content h2, .mw-body-content h3, .mainheader {
    color: #ccc
}
.toc, .mw-warning, .toccolours, div.thumbinner, html .thumbimage, .catlinks, .tabtoc li.selected, .tabtoc li, .tabcontainer {
    border: 1px solid #292929;
    background-color: #0e0b0f;
}

table.wikitable, table.statueslist {
    color:black
}

.border-collapse {
    color: black
}

table.wikitable, table.statueslist {
    color:#ccc
}

a.new {
    color: #ff5656 !IMPORTANT;
}
.headers {
    background-color: #2b1f30 !IMPORTANT;
}

.mw-body .firstHeading {
    display: none
}

#simpleSearch {
    display: none !IMPORTANT
}
}
