css:
@-moz-document domain("instantview.telegram.org") {
.nav-menu.nav-menu-can-fix {
    scrollbar-width: none;
}
ul {
    scrollbar-width: none;
}
.popup {
  background-color: #333;
}
.contest-item-author a {
  color: #dddddd;
}
pre {
    background: #0F192A !important;
}
body {
  background-color: #000;
  color: #ffffff;
}
.contest-rules-status a {
  color: #dddddd;
}
.form-control {
  background-color: #333;
  color: #ffffff;
}
.result-preview-wrap > div {
  background-color: #222 !important;
}
.result-header {
  color: #dddddd;
}
code {
  background: #444 !important;
  border-radius: 5px;
}
main.rules section h3 a.section-header {
  color: #dddddd;
}
header {
  background-color: #333;
}
.header-panel .list-group-template {
  background-color: #222;
}
header .breadcrumb > .active,
header .breadcrumb > .active a {
  color: #dddddd;
}
header .header-wrap {
  box-shadow: none;
}
#result-frame,
#original-frame {
  background-color: white;
}
.list-group-issue-item {
  color: #dddddd;
}
.logged-name,
.list-group-contest-header,
.contest-item-cell {
  color: #dddddd;
}
.contest-item-candidate > a,
.contest-item-winner > a {
  color: #aaaaaa;
}
#url-field {
  color: #ffffff;
}
.rules-header {
  background-color: #222;
}
/* Based on the theme at http://bonsaiden.github.com/JavaScript-Garden */
/*<!--match-->*/
.cm-s-default span.CodeMirror-matchhighlight {
  background: #494949;
}
.cm-s-default.CodeMirror-focused span.CodeMirror-matchhighlight {
  background: #314D67 !important;
}
/*<!--activeline-->*/
.cm-s-default .CodeMirror-activeline-background {
  background: #253540;
}
.cm-s-default.CodeMirror {
  background: #0F192A;
  color: #D1EDFF;
}
.cm-s-default div.CodeMirror-selected {
  background: #314D67;
}
.cm-s-default .CodeMirror-line::selection,
.cm-s-default .CodeMirror-line > span::selection,
.cm-s-default .CodeMirror-line > span > span::selection {
  background: rgba(49, 77, 103, 0.99);
}
.cm-s-default .CodeMirror-line::-moz-selection,
.cm-s-default .CodeMirror-line > span::-moz-selection,
.cm-s-default .CodeMirror-line > span > span::-moz-selection {
  background: rgba(49, 77, 103, 0.99);
}
.cm-s-default .CodeMirror-gutters {
  background: #0F192A;
  border-right: 1px solid;
}
.cm-s-default .CodeMirror-guttermarker {
  color: white;
}
.cm-s-default .CodeMirror-guttermarker-subtle {
  color: #d0d0d0;
}
.cm-s-default .CodeMirror-linenumber {
  color: #D0D0D0;
}
.cm-s-default .CodeMirror-cursor {
  border-left: 1px solid #F8F8F0;
}
.cm-s-default span.cm-comment {
  color: #428BDD;
}
.cm-s-default span.cm-atom {
  color: #AE81FF;
}
.cm-s-default span.cm-number {
  color: #D1EDFF;
}
.cm-s-default span.cm-property,
.cm-s-default span.cm-attribute {
  color: #ccf;
}
.cm-s-default span.cm-keyword {
  color: #f0f;
}
.cm-s-default span.cm-string {
  color: #fbe;
}
.cm-s-default span.cm-builtin {
  color: #76f;
}
.cm-s-default span.cm-variable {
  color: lightblue;
}
.cm-s-default span.cm-variable-2 {
  color: #5af;
}
.cm-s-default span.cm-def {
  color: orange;
}
.cm-s-default span.cm-bracket {
  color: #D1EDFF;
}
.cm-s-default span.cm-tag {
  color: lightgreen;
}
.cm-s-default span.cm-tag-2 {
  color: lightgray;
}
.cm-s-default span.cm-link {
  color: #AE81FF;
}
.cm-s-default span.cm-error {
  background: #F92672;
  color: #F8F8F0;
}
.cm-s-default span.cm-variable-3 {
  color: lightgreen;
}
.cm-s-default .CodeMirror-matchingbracket {
  text-decoration: underline;
  color: white !important;
}

}
