css:
@-moz-document domain("ivwebcontent.telegram.org") {
article:not([class]),
article[class=""],
article[class="--tg-instantview-region-hovered"],
article[class="--tg-instantview-region-selected"],
article[class="--tg-instantview-region-selected --tg-instantview-region-hovered"] {
  background-color: black !important;
  color: #ddd;
  font-family: sans-serif;
}
article:not([class]) [data-block]:after,
article[class=""] [data-block]:after,
article[class="--tg-instantview-region-hovered"] [data-block]:after,
article[class="--tg-instantview-region-selected"] [data-block]:after,
article[class="--tg-instantview-region-selected --tg-instantview-region-hovered"] [data-block]:after {
  background-color: darkgreen;
  color: white;
}
article:not([class]) th,
article[class=""] th,
article[class="--tg-instantview-region-hovered"] th,
article[class="--tg-instantview-region-selected"] th,
article[class="--tg-instantview-region-selected --tg-instantview-region-hovered"] th {
  background-color: #5b5b5b;
}
article:not([class]) td,
article[class=""] td,
article[class="--tg-instantview-region-hovered"] td,
article[class="--tg-instantview-region-selected"] td,
article[class="--tg-instantview-region-selected --tg-instantview-region-hovered"] td {
  background: #333333;
}
article:not([class]) h1,
article[class=""] h1,
article[class="--tg-instantview-region-hovered"] h1,
article[class="--tg-instantview-region-selected"] h1,
article[class="--tg-instantview-region-selected --tg-instantview-region-hovered"] h1,
article:not([class]) h2,
article[class=""] h2,
article[class="--tg-instantview-region-hovered"] h2,
article[class="--tg-instantview-region-selected"] h2,
article[class="--tg-instantview-region-selected --tg-instantview-region-hovered"] h2,
article:not([class]) h3,
article[class=""] h3,
article[class="--tg-instantview-region-hovered"] h3,
article[class="--tg-instantview-region-selected"] h3,
article[class="--tg-instantview-region-selected --tg-instantview-region-hovered"] h3,
article:not([class]) h4,
article[class=""] h4,
article[class="--tg-instantview-region-hovered"] h4,
article[class="--tg-instantview-region-selected"] h4,
article[class="--tg-instantview-region-selected --tg-instantview-region-hovered"] h4 {
  font-family: sans-serif;
}
article:not([class]) section.related h4,
article[class=""] section.related h4,
article[class="--tg-instantview-region-hovered"] section.related h4,
article[class="--tg-instantview-region-selected"] section.related h4,
article[class="--tg-instantview-region-selected --tg-instantview-region-hovered"] section.related h4 {
  font-family: sans-serif;
  background: #333;
  color: white;
}
article:not([class]) section.related a.related-link,
article[class=""] section.related a.related-link,
article[class="--tg-instantview-region-hovered"] section.related a.related-link,
article[class="--tg-instantview-region-selected"] section.related a.related-link,
article[class="--tg-instantview-region-selected --tg-instantview-region-hovered"] section.related a.related-link {
  background: #555;
}
article:not([class]) section.related a.related-link span,
article[class=""] section.related a.related-link span,
article[class="--tg-instantview-region-hovered"] section.related a.related-link span,
article[class="--tg-instantview-region-selected"] section.related a.related-link span,
article[class="--tg-instantview-region-selected --tg-instantview-region-hovered"] section.related a.related-link span {
  color: white;
}
article:not([class]) figcaption > cite,
article[class=""] figcaption > cite,
article[class="--tg-instantview-region-hovered"] figcaption > cite,
article[class="--tg-instantview-region-selected"] figcaption > cite,
article[class="--tg-instantview-region-selected --tg-instantview-region-hovered"] figcaption > cite {
  font-family: sans-serif;
}

}
