css:
@-moz-document domain("gnu.org") {
:root {
    --bg-0: #282828;
    --bg-1: #3c3836;
    --bg-2: #504945;
    --bg-3: #665c54;
    --bg-4: #7c6f64;

    --fg-0: #fbf1c7;
    --fg-1: #ebdbb2;
    --fg-2: #d5c4a1;
    --fg-3: #bdae93;
    --fg-4: #a89984;

    --light-blue: #83a598;
    --light-aqua: #8ec07b;
    --light-green: #b8bb26;
    --light-yellow: #fabd2f;
    --light-orange: #fe8019;
    --light-red: #fb4934;
    --light-purple: #d3869b;

    --dark-blue: #458588;
    --dark-aqua: #689d6a;
    --dark-green: #98971a;
    --dark-yellow: #d79921;
    --dark-orange: #d65d0e;
    --dark-red: #cc241d;
    --dark-purple: #b16286;
    background: var(--bg-0);
}

* {
    font-family: 'Fira Sans', sans-serif;
    color: var(--fg-2) !important;
    box-sizing: border-box;
}

body {
    box-shadow: none;
    -webkit-box-shadow: none;
    background: var(--bg-0);
    padding: 20px;
    color: var(--fg-0);
    line-height: 1.2em;
    min-width: calc(100% - 40px);
    border: none;
}

a {
    line-height: 1.4em;
    color: var(--light-blue) !important;
}
a:hover {
    color: var(--fg-2) !important;
    background: var(--dark-blue) !important;
    border-radius: 3px;
}

h1 {
    border-left: 4px solid var(--light-orange);
    padding: 30px;
    color: var(--fg-0);
}
h1 > a {
    color: var(--light-orange);
    background: none;
    text-decoration: none;
    text-align: center;
    vertical-align: middle;
}

h2 {
    border-left: 4px solid var(--light-yellow);
    padding: 20px;
    color: var(--fg-0);
}
h2 > a {
    color: var(--light-yellow);
    background: none;
    text-decoration: none;
    text-align: center;
    vertical-align: middle;
}

div.header,
div.node {
    margin: 0;
    padding: 0 20px;
}
blockquote {
    margin: 0;
    padding: 10px 40px;
    border-left: 4px solid var(--light-blue);
    background: var(--bg-1);
}

dt {
    padding: 10px;
    border-left: 4px solid var(--light-yellow);
}
#gnu-banner img {
    filter: invert(1);
}
#searcher,
#translations {
    background: var(--bg-2);
}
form.header input[type="text"],
form.header input[type="submit"],
#fsf-links,
#back-to-top a,
ul#edu-navigation {
    padding: 7px;
    background: var(--bg-4);
    border: none;
}
#mission-statement,
#sisters,
#footer {
    border: none;
    background: var(--bg-3);
}
.emph-box,
.summary,
.toc,
#dynamic-duo {
    box-shadow: none;
    -webkit-box-shadow: none;
    background: var(--bg-3)!important;
    border: none;
    border-radius: 20px;
    padding: 20px !important;
    padding-top: 5px!important;
}
#join-fsf a,
#join-fsf a:visited {
    color: var(--light-red);
    background: var(--dark-red);
    border-color: var(--light-red)
}

div.example,
pre.example {
    margin: 1em 0 1em 3%;
    -webkit-border-radius: .3em;
    -moz-border-radius: .3em;
    border-radius: .3em;
    border: none;
    background-color: var(--bg-3);
}
pre.smallexample {
    background: var(--bg-2);
    border-radius: 5px;
    padding: 10px;
    max-width: 100%;
    white-space: pre-wrap;
    /* css-3 */
    white-space: -moz-pre-wrap;
    /* Mozilla, since 1999 */
    white-space: -pre-wrap;
    /* Opera 4-6 */
    white-space: -o-pre-wrap;
    /* Opera 7 */
    word-wrap: break-word;
    /* Internet Explorer 5.5+ */
}
}
