css:
@-moz-document domain("musescore.com"), domain("musescore.org") {
/*--------------------------------Mainelements-----------------------------*/

/*global-inpage-background and header------------------------------------*/
.page, body
{
    color: #cdcdcc;
    background-color: #1c1c1c !important;
    border: 1px #000 !important;
}

a {
    color: #76cdff !important;
}

a:hover, a:focus {
    color: #fff !important;
} 

/* Search Boxes*/
input.form-text {
    color: #fffffffa !important;
    background-color: transparent !important;
    border: solid #0000001c;
    border-width: 0 1px 0 0;
    border-radius: 0 10px 10px 0 !important;
}
/*Banner------------------------------------------*/
header, div > nav  {

    background-image: linear-gradient(#000000d4 60%, #50a6df 69%, #18467f 85%, #4a330b) !important;
    box-shadow: 0 5px 10px #000 !important;
    background-color: #000000;
}

/*-------------------------------Musescore.org---------------------------------------*/

/*Schrift der Seite*/
h1, h3 {
    color: #5696fd !important;
    
}
/*Menueleiste----------------------------------------*/

div.content-header
{
    
    text-shadow: 0 0 5px #0071a4e0 !important;
    background: #141414 !important;
    border: #000000 2px solid;
}

   
/*Menueleiste Schrift*- - - - - - - - - - -*/
.doubletap a
{
    color: #2499fc !important;
    text-shadow: 0 0 10px #00b0ff !important;
}
   

/*Menueleiste Schrift Hover*/
.menu-item:hover a  
{
    color: #ff9d00 !important;
    text-shadow: 0 0 10px #7b4c01 !important;
} 
/*- - - - - - - - - - - - - - - - - - - - - */

/*Menue-Dropdown- - - - - - - - - -*/
.desktop-nav .menu-item ul
{
    
    padding: 5px 0px;
    background: #17171773 !important;
    border: 3px solid #2d2a24 !important;
    border-radius: 20px;
}
.desktop-nav .block-musescore-org-menu li a {
    color: #d2d2d2 !important;
    font-size: 110% !important;
    font-family: "sans-serif";
    font-weight: 500 !important;
    font-variant: small-caps;
    padding: 10px 20px !important;
    border: solid 3px #00a5d2;
    border-bottom: solid 3px#00a3ca !important;
    border-radius: 12px;
    background: #00000082 !important;
}
.desktop-nav .block-musescore-org-menu a:hover {
    background: #004968d1 !important;
    border-radius: 12px;
}

/*Spalte1----------------------------------------------------------*/
.page-front #row1 
{
    background: linear-gradient(#353434,#000) !important;
}

.div.menu-item  a {
    color: #fff !important;
}
/*Spalte1-Download-Button*- - - - - - - - - - - -*/ 
.page-front #row1 .right .button 
{
    color: #d9d9d9 !important;
    background: linear-gradient(#3db800, #00a29e) !important;
}

/*Spalte1-Download-Button*/ 
.page-front #row1:hover .right .button:hover 
{
    color: #fff !important;
    background: linear-gradient(#54ff00, #00fff8) !important;
}
/*- - - - - - - - - - - - - - - - - - - - - - - - -*/

/*Spalte2------------------------------------------------*/
div#row2
{
    background: linear-gradient(#252525, #171616) !important;
}

/*Spalte2-Download-Button*- - - - - - - - - - -*/
.page-front #row2 .button-wrapper .button, .page-front #row2 .button-wrapper .button
{
    background: #00c907 !important;
    color: #000000 !important;
    border: 2px solid #00ff28;
}

/*Spalte2-Download-Button-Hover*/
.page-front #row2 .button-wrapper .button:hover, .page-front #row2 .button-wrapper .button:focus {
    background: linear-gradient(#00a60c,#60ff4c,#008a0a) !important;
    color: #fff !important;
}
/*- - - - - - -  - - - - - - - - - - - - - - - - -*/

/*Spalte3-------------------------------------------------------*/

div#row3 {
    background: linear-gradient( #303030,#101010) !important;
    
}

/*Kaesten- - - - - - - - - - - - - - - - -*/
div.instruments a {
    color: #f2f2f2 !important;
    background: radial-gradient(#70560b ,#000) !important;
    border: solid #b1b1b1;
    border-radius: 20px !important;
    border-width: 2px 0px 2px 0px;
    font-family: "MusescoreIcon" !important;
    font-style: italic;
    font-size: 24px !important;
}
div.instruments a:hover {
    color: #fff !important;
    text-shadow: 2px 2px 2px #000000;
    background: radial-gradient(#a07215 ,#000) !important;
    border: solid #fff;
    border-width: 2px 0px 2px 0px;
    border-radius: 20px !important;
}
a.instrument.marching /*'Marchmusik' Schriftgröße*/
{
    font-size: 24px !important
}
/*- - - - - - - - - - - - - - - - - - - - - */

/*Spalte5---------------------------------------------*/

div#row5 {
    background: #000000 !important;
}
/*Karten- - - - - - - - - - - - - - -*/
.page-front #row5 .block-wrapper div
{
    color: #fff;
    background: linear-gradient(#555555,#222121,#112344);
} 
/*- - - - - - - - - - - - - - - - - - -*/

/*Button 'Mitwirken- - - - - - - - - - - - - - - - - -*/
page-front #row5 .button-wrapper .button, .page-front #row5 .button-wrapper .button
{
    color: #fff !important;
    background: radial-gradient(#cc4e00,#522231) !important;
    border: solid #fff 5px !important;
    border-radius: 10px !important;
}

page-front #row5 .button-wrapper .button:hover, .page-front #row5 .button-wrapper .button:hover
{
    color: #fff !important;
    background: radial-gradient(#fa0,#1f0100) !important;
    border: solid #ff7b00 5px !important;
}    
/*- - - - - - - - - - - - - -  - - - - - - - -  - -  -*/

/*Button Download- - - - - - - - - - - - - - - - - -*/
.page-front #row7 .button, .page-front #row7 .button
{
   color: #d9d9d9 !important;
    background: linear-gradient(#3db800, #00a29e) !important;
}

.page-front #row7 .button:hover, .page-front #row7 .button:focus {
    color: #fff !important;
    background: linear-gradient(#54ff00, #00fff8) !important;
}
/*- - - - - - - - - - - - - - - -  -- - - - - - - - - -*/

/*Musescore.org > Soundfonts-----------------------------------*/

/*Side Box*/
.aside-col-second {
    filter: invert(90%)
}
/* Musescore Course*/

.block-musescore-org-book-cover {
    filter: invert(20%);
    filter: hue-rotate(65deg);
    background: linear-gradient(#a3a3a3,#bcbcbc) !important;
    
}
/*Handbuch PDF Icon*/

#handbook-pdf-link img {
    filter: invert(100%)
}

#handbook-pdf-link a:hover, #handbook-pdf-link a:focus {
    color: #000 !important;
}
/*Musescore.org > Plugins---------------------------------------*/

/*alternating background*/
table tr:nth-child(2n+1) td {
    background: #313131;
}

/*Dropdown-menus*/

.bootstrap-select > .dropdown-toggle {
    
    color: #fff;
    background: radial-gradient(#464645,#000);
    border: solid #fff;
    border-width: 1px 0px 1px 0px;
    border-radius: 10px;
    font-weight: bold;
}
.bootstrap-select.btn-group .dropdown-toggle .filter-option {
    text-align: center;
    font-size: 18px !important;
    font-variant: small-caps;
}

.dropdown-menu {
    background: #000000c4;
    border: solid 2px #fff;
    border-radius: 20px;
}
.dropdown-menu > li > a:hover, .dropdown-menu > li > a:focus {
    background: radial-gradient(#502300,#000);
}

.warning {
    color: #ff8181 !important;
    background: #340000 !important;
}

/*Musescore.org > Tutorials*/
.getting-started {
    background: #000000;
}
/*Musescore.org > Issue Tracker*/
.view-issues tr.state-14 td, .project-issue-status-14 a {
    background: #2d3517;
}
.view-issues tr.state-13 td, .project-issue-status-13 a {
    background: #230303;
}

/*Musescore.org > Donations*/
.donation-status td.white{
    background: linear-gradient( #000000, #626262) !important;
}
.donation-status td.green{
    background: linear-gradient( #6f0, #194a00) !important;
}

#stripe-donate-form {
    background: #000000;
    border: solid 2px #55b200;
}
/*---------------------------------Musescore.org ENDE---------------------------*/

/*---------------------------------Musescore.com---------------------------------*/

/*Login-------------------*/

.social_auth--button {
    
    background-color: #ffffff29 !important;
    filter: invert(80%)contrast(90%)!important;
    border: 1px solid #fdebeb !important;
    border-radius: 10px;
    background-size: 44px 44px !important;
}

.social_auth--button:hover, .social_auth--button:focus {
    
    background-color: #ffffff29 !important;
    filter: contrast(160%) !important;
}   
.separator > p /* OR-Button*/

{
    color: #dbdbdb;
    font-size: 20px;
    font-weight: bold !important;
    border: 2px solid #d6d6d6;
    background: #291f02 !important;
    border-radius: 8px;
}
/*email BOX*/
body.page-boxed-content .box input {
    border-color: #ff8000;
    border-width: 0px 2px 0px 2px !important;
    border-radius: 10px !important
}


.clearfix
{
    color: #ff8600;
    background: #0e0e0e !important;
}

.clearfix input#edit-submit.button
{
    background: #2b8502 !important;

}


/*--------------------------------Cookie-message-------------------------------------------------*/
#js-baremetrics.desktop.page-boxed-content > div * {
    filter: invert(100%);
}

nav * {
    filter: invert(98%) !important;
    border-radius: 10px !important;
}

/*-------------Button-Hover------------------------*/

button:hover{
    background: #a3f0ff96 !important;
}

/*------------------title bar----------------*/
th {
    color: #fff !important;
    background: #488238 !important;
 
}

/*-----------------side column----------------------*/
aside {
    background: #000000 !important
}

tr * {
    color: white !important;
}

/*-----------------------music sheets------------BREAKS LOGIN ICONS-------*/

section > main > div * {
    background: #00000045!important;
}

/*--------------------------boxes---------------------------------*/
a#all * {
    filter: invert(100%) !important;
    background: #514c46 !important;
}

/*------------------------Pop Ups--------------------------*/

article > section * {
    color: #dfb2a2 !important;
    background: #222 !important;
}

article > section * svg {
    background: #fff !important;
}
 
span {
    background: #05050529 !important;
}

.nav-wrapper * {
    background: #000 !important;
} 

}
