css:
@-moz-document domain('ux.getuploader.com') {

html, body {
    background-color: #060606 !important;
}

body { 
    color: #bababa;
}

a {
    color: #07a !important;
}

    a:visited {
        color: #0077aa !important;
    }

.navbar, .container {
    background-color: #121212;
}

.btn, .btn-default {
    background-color: #313131 !important;
    color: #949494 !important;
}

/* Download Indexes */
.form-control {
    background-color: black;
}

.table > tbody > tr:hover,
th {
    background-color: #343434;
}

.pagination li a {
    background-color: #343434;
}

    .pagination li.active a {
        background-color: #585858;
        border: 1px solid #ddd;
    }
    
        .pagination li.active a:hover {
            background-color: #585858;
            border: 1px solid #ddd;
        }

.panel {
    background-color: #343434 !important;
}

.panel-info > .panel-heading {
    background-color: #9fb0b9;
}

.panel-warning > .panel-heading {
    background-color: #cac4a2;
}

#google_translate_element {
    display: none; /* Who even reads 英語？ そのたちはバカ外人ですね。*/
}

/* Download Pages */
.form-group .form-control {
    background-color: #343434;
    color: #949494;
}

div span:first-child b {
    background-color: #323232;
    display: none !important; /* Remove this line if you want to support the site */
}

}
