css:
@-moz-document url-prefix("https://docs.lucee.org") {
html, body {
    background-color: #1F1F1F;
}

.menu-wrap, .menu-content, .menu-content .nav a {
    color: #9e9e9e;
    background-color: #1F1F1F;
}

.menu-content .nav a:hover {
    background-color: #ccc;
    color: #1F1F1F;
}

.content,
.footer {
    background-color: #1F1F1F;
    color: #9e9e9e;
}
.tile,
.body table>thead th,
.body table td, pre {
    background-color: #181818;
    color: #9e9e9e;
}
.tile, .content-heading {
    -webkit-box-shadow: 0 0 0 #e0e0e0, 0 0 2px rgba(0, 0, 0, 1), 0 2px 4px rgba(0, 0, 0, 1);
    		box-shadow: 0 0 0 #e0e0e0, 0 0 2px rgba(0, 0, 0, 1), 0 2px 4px rgba(0, 0, 0, 1);
}

.o {
    color: inherit;
}

.nt, .k {
	color: #0086B3;
}

.m {
	color: #f44336;
}

.err, span.oembedall-closehide {
	background-color: inherit;
}

.header-logo img, .nav-drawer-logo img {
	filter: invert(100%);
    -webkit-filter: invert(100%);
    		filter: invert(100%);
}

.form-control {
	color: #9e9e9e
}
}
