@-moz-document domain("rockauto.com") {
/* Site Settings */

* {
	letter-spacing: 1px!important;
	text-decoration: none!important;
	transition: none!important;
}

.filter-dropdown-header,
.graybubble,
.listing-container-border,
.listing-quantity-adjustment .qty-adjust-button,
.plus_x_days .plouter,
.ra-tab-header-active .outer,
.ra-tab-header-inactive .outer,
.ra-widget-panel-c,
.ra-widget-panel-t,
.remove-for-print,
.whitebubble,

:focus,
[style="border: 1px solid #dcdcdc;"],
a.ra-btn-moreinfo, a.ra-btn-moreinfo:active, a.ra-btn-moreinfo:focus
{
	border: none!important;
	box-shadow: none!important;
	outline: none!important;
}

.plus_x_days .plouter {
	font-weight: 700;
}

/*___ Fonts ________________________________________________________________________*/

.bdydesc,
.ra-formatted-amount span,
	
[class*='listing-text-row-moreinfo-']
{
	font-size: 31px!important;
}

.listing-final-partnumber,
.mi-body,
.mo-link,
.ramobile .fl-row-c .inner a,
.ramobile .fl-row-c .inner,
.ramobile .fl-row-c-inner,
.vmiddle,
	
body,
label,
table.overlaypartcomparison td
{
	font-size: 21px!important;
}

.graybubble
{
	font-size: 17px!important;
}

.bdytext li
{
	line-height: 25px!important;
}

/*___ Left/Right/Top/Bottom ________________________________________________________*/

#overlayinner
 {
    left: 460.25px;
}
	
.ranavouter .inner .nlabel .navlabellink {
    margin-top: 6px;
}

/*___ Margins ______________________________________________________________________*/

.bdytext li,
.bdytext,
.listing-container-border
{
	margin: 12px!important;
}

/*___ Paddings _____________________________________________________________________*/

.bdytext,
.listing-container-border
{
	padding: 12px!important;
}

table.overlaypartcomparison td {
    padding: 5px 14px;
}

.ra-widget-panel-t
{
	padding: 8px 20px!important;
}

/*___ Widths _______________________________________________________________________*/

#side-cart,

.ra-widget-panel-c
{
	width: auto!important;
}

}

