css:
@-moz-document url-prefix("https://lovleg.p3.no/") {
/* Un-hides the Notes icon */
.show-for-small-only {
    display: block !important;
}
/* Put the open notes on the top of the z-index */
.custom_type_notes-template-default {
    z-index: 999;
}
/* Fixes positioning of the open notes for the bigger screen */
.custom_type_notes_header {
    margin: 20px auto -50px auto;
    position: relative;
    width: 400px;
    padding: 0;
}
/* Un-hides the Notes screen */
.show-for-small-only, .hide-for-small-up, .show-for-small, .show-for-small-down, .show-for-medium-only, .hide-for-medium-up, .show-for-medium, .show-for-medium-down, .hide-for-large-only, .hide-for-large-up, .hide-for-large, .hide-for-large-down, .show-for-xlarge-only, .show-for-xlarge-up, .show-for-xlarge, .hide-for-xlarge-down, .show-for-xxlarge-only, .show-for-xxlarge-up, .show-for-xxlarge, .hide-for-xxlarge-down {
    display: block !important;
}
/* Fixes how the icon is displayed on wider screen resolutions */
.profil .d19-profil-meta .social img {
	float: left;
    margin-top: 0.15em;
}
}
