css:
@-moz-document url-prefix("https://www.dr.dk/drtv/") {
/* Logo farve */
#Fill-1, .profile-selector__logo-wrap .svg-icon.brand-logo path:nth-child(2) {
    fill: #e93f33 !important;
}

/* Ikoner */
.bookmark-btn .cta-toggle-btn .svg-icon path {
    fill: rgba(255,255,255,0.6) !important;
    stroke: rgba(255,255,255,0.6) !important;
}

/* Borders */
.drh6-hero .carousel-item:after, .drop-menu__content {
    border-color: #121212 !important;
}
.drop-menu:after {
    border-color: #333333 !important;
}
.egp4-channel__schedule, .epg1__logos--logo, .schedule {
    border-color: #333333 !important;
}
/* Mørk bg 1 */
body, .header, .dr-egp-text__row, .drh6-hero .carousel-text, .vertical-nav, .drop-menu__content, .drop-menu:after, .item-detail, .item-detail .dh1-drtv-hero__info>div, .item-detail .dh1-drtv-hero__info>h1, .item-detail .dh1-drtv-hero__info>p, .d10-info, .profile-selector, .channel-detail, .egp4-channel__channel-logo, .egp4-channel__schedule, .epg-day-picker .date-element--active, .epg1__logos--logo, .schedule, .epg-day-picker .arrow, .drtv-drop-select__options-drop-down {
    background: #121212 !important;
}

/* Mørk bg 2 */
.epg-timeline__time, .epg-day-picker {
    background: #2d2d2d !important;
}

/* Rød tekst */
#channelGroup_filter_all {
    color: #e93f33 !important;
}

/* Lys tekst 1 */
.entry-title__link, .h1, .h2, .h3, .h4, .h5, .h6, h1, h2, h3, h4, h5, h6, .drh6-hero .carousel-text__tagline, .drh6-hero .carousel-text__title, .vertical-nav-group>.nav-entry-link--group, .vertical-nav-group>.nav-entry-link--item, .vertical-nav-group>.nav-entry-link--primary, .vertical-nav, .vertical-nav__switch-button, .drop-menu__content span, .dh1-item-desc__short-description, .dh1-drtv-hero__episode-metadata, .bookmark-btn .cta-toggle-btn__label, .selector__item>.profile-circle, .profile-circle__label--type, .digit-input, .pin-group__view-pin, .cta-btn--naked, .channel-detail, .ch1-standard, .egp4-channel__schedule, .epg-day-picker .date-element--active, .drtv-drop-select__current-option p, .channel-group-selection.drtv-drop-select .drtv-drop-select__option {
    color: rgba(255,255,255,0.8) !important;
}

/* Lys tekst 2 + ikoner */
.header__icon-btn, .dr-egp-text__text, .epg-timeline__time, path.svg-arrow, .drtv-drop-select__arrow-icon {
    color: rgba(255,255,255,0.6) !important;
    fill: rgba(255,255,255,0.6) !important;
}

/* Lys tekst 3 */
.vertical-nav__dr-global-navigation ul li a, .vertical-nav-group .nav-entry-link--group, .vertical-nav-group .nav-entry-link--item, .vertical-nav-group .nav-entry-link--primary, .egp4-channel__schedule+.egp4-channel__schedule .egp4-channel__time {
    color: rgba(255,255,255,0.4) !important;
}

/* Mørk tekst 1 */
.cta-btn--secondary-black {
    color: #121212 !important;
}

/* Knapper lys */
button.cta-btn.cta-btn--dark.cta-btn--secondary.cta-btn--secondary-dark.truncate {
    background-color: rgba(255,255,255,0.8) !important;
    color: #121212 !important;
}
button.cta-btn.cta-btn--dark.cta-btn--secondary.cta-btn--secondary-dark.truncate:hover {
    background-color: rgba(255,255,255,1) !important;
    color: #000000 !important;
}

/* Hvid fill */
.profile-circle__lock svg {
    fill: #ffffff !important;
}

/* Hvid box-shadow */
.digit-input {
    box-shadow: inset 0 -2px 0 -1px rgba(255, 255, 255, 0.8) !important;
}

/* Hover + active tekst */
.primary-nav__entry--active a, a:hover, .d1-drtv-season-list__item--active .d1-drtv-season-list__link {
    color: #ffffff !important;
}

/* Lys baggrund 1 */
.primary-nav__entry--active:before, .cta-btn--secondary-black {
    background-color: rgba(255,255,255,0.8) !important;
}

/* Kategorinavn header image justering */
/*
.h9__picture:before {
    content: ' ';
    position: absolute;
    width: 100%;
    height: 100%;
    top: 0;
    left: 0;
    background-color: rgba(255, 255, 255, 0.93);
    mix-blend-mode: difference;
}
*/
.h9__picture {
    opacity: 0.9;
}
.egp4-channel__next-item-list .pi-arrow {
    fill: rgba(255,255,255,0);
}
}
