css:
@-moz-document domain("watch.nba.com") {
body {
    background-color: #0a0a0a;
}

.container .schedule-list {
    background-color: #0f0f0f;
    color: #999;
}

.container .schedule-list .schedule-item {
    background-color: #0f0f0f;
    border-color: #0f0f0f;
    color: #999
}
.container-left .tab-class {
    background-color: #0f0f0f;
    border-color: #0f0f0f;
    color: #999;
}

.schedule-calendar .calendar-date {
    background-color: #0f0f0f;
    border: #0f0f0f;
    color: #999;
}

.container .schedule-list .schedule-item .game-info .game-situation {
    display: inline-block;
    padding: 10px 0;
    color: #008860;
    width: 49%;
    vertical-align: middle;
    box-sizing: border-box !important;
    font-size: 14px;
}

.game-container .schedule-list .schedule-item .game-info .game-playing .game-situation {
    display: inline-block;
    vertical-align: middle;
    padding: 10px;
    font-size: 15px;
    color: #008860;
    box-sizing: border-box;
}

.schedule-calendar .prev-day,
.schedule-calendar .next-day {
    background-color: #0f0f0f;
    color: #999;
}
.switch-part .item {
    background-color: #0a0a0a;
    color: #999;
}

.score-switcher {
    background-color: #0f0f0f;    
}

.page_footer {
    background-color: #0a0a0a;
    color: #999;
}

.header-master {
    background-color: #0a0a0a;
    color: #999;   
}

.header-master .extend-highlight:before {
    content: "";
    position: absolute;
    left: 0;
    top: 0;
    height: 100%;
    background-color: #0a0a0a;
    width: 0;
}
#headerExtendHighlight {
    background-color: #0a0a0a;
}

#menuStats {
    background-color: #0f0f0f;
}

#gameWatchListen .game-watch, #gameWatchListen .game-listen {
    font-size: 14px;
    font-family: 'Roboto-Regular', sans-serif;
    color: #FFF;
    width: 138px;
    height: 36px;
    line-height: 36px;
    text-align: center;
    border-radius: 5px;
    cursor: pointer;
    background-color: #151515;
    margin: 7px 5px;
    display: inline-block;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
}

.button-account {
    background-color: #0a0a0a;
}

.container-account .container-extend {
    background-color: #0f0f0f;

}

.header-master .container-extend {
    background-color: #0F0F0F;
    position: absolute;
    z-index: 999;
    color: #CCC;
    transition: 200ms cubic-bezier(0.930, 0.210, 0.005, 0.890), width 200ms 0ms,height 0ms 200ms;
    width: 320px;
}

.header-master .container-extend .search-input.extend-menu-item {
    display: block;
    background-color: #151515;
    height: 50px;
    margin: 60px auto 0 auto;
    border-radius: 8px;
    padding-left: 25px;
    position: relative;
}

.header-master .header-right .search-button .search-position:last-child {
    background-color: #0A0A0A;
}

#over-hidden .container-extend {
    background-color: #0f0f0f;
}

#scheduleShowsList   {  
    border-color: #0a0a0a;
    color: #999;
}

.onoffswitch-inner:after {
    content: "OFF";
    padding-right: 9px;
    background-color: #303030;
    color: #9b9b9b;
    text-align: right;
}

.onoffswitch-inner:before {
    content: "ON";
    padding-left: 10px;
    background-color: #1d428a;
    color: #CCC;
    text-align: left;
}
.onoffswitch-label
{
	display: block;
	overflow: hidden;
	cursor: pointer;
	border: 1px solid #0F0F0F;
	-moz-border-radius: 20px;
	-webkit-border-radius: 20px;
	border-radius: 20px;
	margin: 0;
}

#gameHeaders {
    background-color: #0a0a0a;
}

#gameWatchListen {
    background-color: #0a0a0a;
}

.button-extend {
    background-color: #0a0a0a;
}

.over-hidden .container-extend {
    background-color: #0a0a0a;
}

.NBA-logo {
    background-color: #0a0a0a;
}

.game-tracker-content .score-scale {
    width: 40px;
    height: 100%;
    float: left;
    background: none !important;
}


.game-tracker {
    background-color: #101010;
    color: #999;
}

.game-tracker-chart .quarter:not(:first-child):before
{
	content: "";
	position: absolute;
	width: 1px;
	left: 0;
	height: 100%;
	background-color: #202020;
}
.game-tracker-chart .quarter .quarter-inner
{
	font-size: 14px;
	letter-spacing: normal;
	position: absolute;
	bottom: 0;
	text-align: center;
	width: 100%;
	font-family: 'Roboto-bold', sans-serif;
	line-height: 32px;
}
.game-tracker-chart .quarter .quarter-inner.break div
{
	display: none;
}
.game-tracker-canvas {
	position: absolute;
	width: 100%;
	height: 225px;
	top: 0;
	z-index: 2;
}
.game-tracker-canvas .tracker-row
{
	list-style: none;
	padding: 0;
	position: absolute;
	width: 100%;
	height: 100%;
	z-index: -2;
}
.game-tracker-canvas .tracker-row li
{
	width: 100%;
	position: relative;
	height: 16.7%;
}
.game-tracker-canvas .tracker-row li:before
{
	content: "";
	width: 100%;
	height: 1px;
	position: absolute;
	background-color: #202020;
	bottom: 0;
}
.game-tracker-canvas .tracker-row li span
{
	position: absolute;
	height: 20px;
	color: #CCC;
	display: block;
	letter-spacing: normal;
	font-size: 14px;
	right: 100%;
	bottom: -10px;
	text-align: center;
	width: 40px;
	font-family: 'Roboto-bold', sans-serif
}
.game-tracker .game-tracker-title
{
	line-height: 50px;
	text-align: center;
	border-bottom: 1px solid #202020;
	font-size: 18px;
}
.game-tracker .game-tracker-biggestlead
{
	line-height: 40px;
	height: 42px;
	border-top: 1px solid #202020;
}
/*start qauter-score*/
div.qauter-score
{
	max-width: 750pt;
	background: #0F0F0F;
	color: #CCC;
}
div.qauter-score table
{
	border-collapse: collapse;
}
div.qauter-score td,
div.qauter-score th
{
	width: 140px;
	height: 36px;
	border: 1px solid #151515;
	text-align: center;
}
div.qauter-score th
{
	border-top: none;
}
div.qauter-score th:first-child
{
	border-left: none;
}
div.qauter-score td.team
{
	width: 300px;
	height: 40px;
	border-left: none;
}
div.qauter-score td.team .team-wrap
{
	box-sizing: border-box;
	padding: 0 0 0 10px;
	width: 100%;
	height: 100%;
	text-align: left;
	line-height: 38px;
	font-size: 14px;
}
div.qauter-score td.team .team-name-small
{
	display: none;
}
div.qauter-score td.team .team-wrap .team-logo
{
	display: inline-block;
	width: 40px;
	height: 40px;
	background-size: 100%;
	background-position: center center;
	background-repeat: no-repeat;
}
div.qauter-score td.team .team-wrap .team-name
{
	display: inline-block;
	vertical-align: top;
	margin-left: 6px;
	line-height: 40px;
}
div.qauter-score tr td:last-child,
div.qauter-score tr th:last-child
{
	border-right: none;
	font-weight: bold;
}
@media (max-width: 880px)
{
	div.qauter-score td.team .team-wrap
	{
		display: none;
	}
	div.qauter-score td.team .team-name-small
	{
		display: block;
		font-weight: bold;
	}
	.qauter-score .quarter-header
	{
		background: #f7f7f7;
	}
	.has-more-quarter
	{
		font-size: 12px;
	}
}

.hide-querter.team-leaders {
    background-color: #101010;
    color: #999;
}


.team-leaders-item-footer {
    background-color: #131313;
    color: #999;
}

.team-leaders-content .team-leaders-item .team-leaders-item-footer span.point {
    background:none;
    position: absolute;
    left: 50%;
    width: 138px;
    line-height: 42px;
    text-align: center;
    transform: translateX(-50%);
    -webkit-transform: translateX(-50%);
    -ms-transform: translateX(-50%);
    -moz-transform: translateX(-50%);
    font-weight: bold;
}

.team-leaders-content .team-leaders-item .team-leaders-item-footer div a {
    color: #AAA;
    display: inline-block;
    transform: translateX(-20px);
    -webkit-transform: translateX(-20%);
    -ms-transform: translateX(-20%);
    -moz-transform: translateX(-20%);
    text-transform: capitalize;
    text-decoration: none;
}

.point {
    background-color: #131313;
    color: #999;
}


.container .schedule-list .schedule-item .game-info .team-league,
.game-container .schedule-list .schedule-item .game-info .team-league,
.video-carousel .video-card .desc-lines,
.video-card .card-time-topics {
    color: #999;
}
.game-container .schedule-list {
    background-color: #0a0a0a;
    
}

.card-folders, .list-shows, .schedule-content {
    background-color: #0A0A0A;
}

.card-list .video-card .card-func {
    padding: 9px 14px;
    border-top: 1px solid #0F0F0F;
    background-color: #0F0F0F;
}
.video-card.list-item .card-func, .video-card.list-item.left-right .card-func, .video-card.list-item.left-right.up-down .card-func {
    padding: 9px 14px;
    border-top: 1px solid #151515;
}
.video-card .card-func {
    padding: 13px 18px;
    border-bottom: 1px solid #151515;
}

.carousel-header {
    color: #DDDDDD;
}
.game-container .calendar-content .calendar-list,
.game-container .calendar-content .schedule-titles,
.game-container .schedule-list .schedule-item .item-round,
.game-container .schedule-list .schedule-item .team-info,
.game-container .schedule-list .schedule-item .game-info
 {
    color: #999;
    background-color: #151515;
}
.game-container .schedule-titles .schedule-titles-left a.selected,
.game-container .schedule-titles .schedule-titles-right a.selected {
    color: #999;
}
.game-container .schedule-titles .schedule-titles-left a,
.game-container .schedule-titles .schedule-titles-right a {
    color: #999;
}
.game-container .calendar-list .calendar-btn,
.video-card .card-wrapper {
    background-color: #151515;
}
.game-container .calendar-list .calendar-btn span,
.game-container .calendar-list .calendar-list-item a {
    color: #66a0ff;
}
.game-container .score-on .desc,
.game-container .calendar-list .calendar-list-item a.selected {
    color: #DDDDDD;
}

.game-container .schedule-list .schedule-item {
    display: inline-block;
    position: relative;
    width: 490px;
    min-height: 240px;
    margin-top: 20px;
    box-sizing: border-box;
    vertical-align: top;
    background-color: #151515;
    color: #000;
    overflow: hidden;
}

.game-container .calendar-content {
    margin: 0 auto;
    box-shadow: none;
}

.game-container .calendar-content .schedule-titles {
    width: 320px;
    border-right: 1px solid #202020;
    background: #151515;
}

.container .schedule-calendar {
    height: 50px;
    background-color: #0f0f0f;
    border-bottom: 1px solid #202020;
}

.header-master .container-extend .extend-menu-item.menu-drop-down {
    background-color: #0F0F0F;
}

.schedule-list .schedule-item .over {
    position: absolute;
    top: 0;
    width: 100%;
    height: 100%;
    padding: 10px 0;
    box-sizing: border-box !important;
    text-align: center;
    background: #101010;
    color: #CCC;
    z-index: 100;
    cursor: default;
    display: block;
    transition: left 200ms cubic-bezier(0.930, 0.210, 0.005, 0.890), top 200ms cubic-bezier(0.930, 0.210, 0.005, 0.890);
}

.header-master .extend-highlight {
    background-color: #0A0A0A;
    float: left;
    margin-right: 20px;
    position: relative;
    height: 100%;
}

.search-full input[type="search"] {
    border: none;
}
.search input[type="search"] {
    background-color:#101010;
    border: none;
    border-radius: 30px;
    box-sizing: border-box;
    color: #CCC;
    height: 40px;
    padding-left: 40px;
    padding-right: 20px;
    -webkit-appearance: none;
    width: 100%;
}

.search::before {
    position: relative;
    top: 50%;
    transform: translateY(-50%);
    background: none;
    color: #ddd;
    content: "\1F50D";
    font-size: 18px;
    position: absolute;
    left: 15px;
}

body {
    background-color: #0A0A0A;
    color: #CCC;
    font-family: 'Helvetica Neue', Arial, Helvetica, sans-serif;
    font-size: 15px;
    font-weight: 400;
    line-height: 1.5;
}

.promoted-articles-item a {
    border-bottom: 1px solid #101010;
    color: #CCC;
    display: block;
    padding: 15px 0;
}

.promoted-articles-item:last-child a {
    border-bottom: 1px solid #101010;
}

.footer {
    border: none;
    margin-top: 60px;
    padding: 30px 0;
}

.page_footer .footer-info {
    border-top: 0px solid #dddddd;
    padding-top: 20px;
}

.page_footer a {
    color: #1888BC;
    text-decoration: none;
    line-height: inherit;
    cursor: pointer;
}

.game-headers .game-hero {
    height: 136px;
    position: relative;
    background: #0F0F0F;
    font-size: 0;
    padding: 22px 15px;
    color: #CCC;
}

.game-live .game-state {
    font-size: 20px;
    color: #E0245E;
    width: 40%;
}
}
