css:
/* AGENT_SHEET */
@namespace html url(http://www.w3.org/1999/xhtml);

@namespace xul url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);


/*----- SITES TO EXCLUDE -----*/
@-moz-document regexp("https?://(?!(www.your.sites.here.com|forum.example.com)).*"), url-prefix(ftp://), url-prefix(about), url-prefix(javascript)
{
 
    
    
  html
  {
    background: #fbf1c7 !important;
  }


/*----- DEFAULT TEXT, BORDER & BACKGROUND COLORS -----*/
  *
  {
    color: #282828 !important;
    box-shadow: none !important;
    background-color: #fbf1c74f !important;
 /*   border-color: #444 !important;
    border-top-color: #444 !important;
    border-bottom-color: #444 !important;
    border-left-color: #444 !important;
    border-right-color: #444 !important */;
  }

  *:before, *:after
  {
    background-color: transparent !important;
 /* border-color: #444  !important */;
  }

  html *
  {
    color: inherit !important;
  }

  p::first-letter,z
h1::first-letter,
h2::first-letter,
p::first-line
  {
    color: inherit !important;
    background: none !important;
  }

/* :: Give solid BG :: */

/* element */
  b,i,u,strong
  {
    color: #98971a
  }

  html, 
body,
li ul,
ul li,
table,
header,
article,
section,
nav,
menu,
aside,

/* common */

[class*="nav"],
[class*="open"],
[id*="ropdown"], /*dropdown*/
[class*="ropdown"], 
div[class*="menu"],
[class*="tooltip"],
div[class*="popup"], 
div[id*="popup"],

/* Notes, details, etc.  Maybe useful */
div[id*="detail"],div[class*="detail"],
div[class*="note"], span[class*="note"],
div[class*="description"],

/* Also common */
div[class*="content"], div[class*="container"],

/* Popup divs that use visibility: hidden and display: none */
div[style*="display: block"], 
div[style*="visibility: visible"]
  {
    background-color: #fbf1c7 !important;
  }



/* Highlight */
  ::-moz-selection
  {
    background-color: #3c3836 !important;
    color: #fbf1c7 !important;
  }

/* ::: anchor/links ::: */

    

  a:link
  {
    color: #458588 !important;
  } /* hyperlink */
  a:visited
  {
    color: #b16286 !important;
  }

  a:active
  {
    color: #d65d0e !important;
  }

    
    
    
/* "Top level" div */

  body > div
  {
    background-color: inherit !important;
  }

    
    
/* :::::: Text Presentation :::::: */

  summary, details
  {
    background-color: inherit !important;
  }

  kbd, time, label, .date
  {
    color: #98971a !important;
  }

  acronym, abbr
  {
    border-bottom: 1px dotted !important;
    cursor: help !important;
  }

  mark,code,pre,samp,blockquote
  {
    background-color: #d5c4a1 !important;
  }


/* :::::: Headings :::::: */

  h1,h2,h3,h4,h5,h6
  {
    background-image: none !important;
    border-radius: 5px !important;
/* -moz-border-radius: 5px !important; */
    -webkit-border-radius: 5px !important;
    text-indent: 0 !important;
  }

  h1,h2,h3,h4,h5,h6
  {
    background-color: #d5c4a1 !important;
  }

  h1,h2
  {
    color: #98971a!important;
  }

  h3,h4
  {
    color: #d65d0e !important;
  }

  h5,h6
  {
    color: #d65d0e!important;
  }

    
    
/* :::::: Tables, cells :::::: */

  table table
  {
    background: #d5c4a1 !important;
  }

  th, caption
  {
    background: #ebdbb2 !important;
  }

/* ::: Inputs, textareas ::: */

  input, textarea, button,
select,option,optgroup
  {
    color: #fbf1c7 !important;
    background: none #d5c4a1 !important;
    -moz-appearance: none !important;
    -webkit-appearance: none !important;
  }

  input,
textarea, 
button
  {
    border-color: #fbf1c7 !important;
    border-width: 1px !important;
  }





/* Lightbox fix */
  html [id*="lightbox"] *
  {
    background-color: transparent !important;
  }

  html [id*="lightbox"] img
  {
    opacity: 1 !important;
  }

/* Youtube Annotation */
  #movie_player-html5 .annotation
  {
    background: #d5c4a1 !important;
  }

/* Mozilla addons shrink/expand sections */
  .expando a
  {
    background: none transparent  !important;
  }
    
 /*----- IMAGE CHANGES -----*/

    
  img
  {
    opacity: .7 !important;
    transition: opacity .2s;
  }

  img:hover, a:hover img
  {
    opacity: 1 !important;
  }
}    



   
/************************/

    
/* TABS, url bar and navbar*/


#main-area, textbox
{
  background: #fbf1c7 !important;
  color: #3c3836 !important;
}



/* autocomple url box */

.autocomplete-richlistbox
{
  background: #d5c4a1 !important;
}

#PopupAutoCompleteRichResult .autocomplete-richlistitem description
{
  color: #689d6a !important;
  text-decoration: underline;
  font-size: 11px;
}

#PopupAutoCompleteRichResult .autocomplete-richlistitem description:last-child
{
  color: #504945 !important;
  font-weight: normal;
  text-decoration: none;
  font-size: 12px;
}

.ac-title
{
  color: #3c3836 !important;
}



/*************/

tab
{
  color: #3c3836 !important;
}

.tabbrowser-tab *
{
  background: #d5c4a1 !important;
  margin: 0px /* 1px */ !important;
}

.tabbrowser-tab[selected="true"] .tab-stack
{
  color: #3c3836 !important;
  border-radius: 0px !important;
  position: relative !important;
  margin: 0px /* 1px */ !important;
  border-top: 4px solid #d65d0e  !important;
}

#nav-bar
{
  background: #d5c4a1 !important;
}
    
/************************/

    
    
    

/*----- ABOUT PAGES BACKGROUND -----*/
@-moz-document url-prefix(about)
{
  #newtab-window, #addons-page
  {
    background: #d5c4a1 !important;
  }

  *
  {
    background-color: transparent;
  }

  body > *
  {
    background-color: rgba(0,0,0,.5) !important;
  }

  #list-view
  {
    background: #d5c4a1 !important;
  }

  #detail-view
  {
    background: #d5c4a1 !important;
  }

  #addons-page > * > *, #addons-page > * > * > *
  {
    border-color: #d5c4a1 !important;
  }
}

/*----- RIGHT-CLICK MENUS & TOOLTIP STYLE -----*/
#contentAreaContextMenu menuitem[disabled="true"], 
#contentAreaContextMenu menuitem[disabled="true"] menuseparator
{
  display: none !important;
}

menupopup, popup,
popup > menu > menupopup,
menupopup > menu > menupopup
{
  -moz-appearance: none !important;
  background: rgba(15,15,15,.9) !important;
  border: 1px solid #fbf1c7 !important;
  border-radius: 3px !important;
  box-shadow: inset 0 0 1px #a89984 , inset 0 0 10px #fbf1c7 !important;
  padding: 2px 0 !important;
}

menupopup *
{
  box-shadow: none !important;
  color: #a89984 !important;
}

menupopup menuitem:hover *
{
  color: #282828 !important;
  text-shadow: 0 0 5px rgba(255,255,200,0.9) !important;
}

menupopup menuseparator
{
  -moz-appearance: none !important;
  background: transparent !important;
  margin: 2px 0 2px -3px !important;
  padding: 0 !important;
  border-top: none !important;
  border-color: transparent !important;
  box-shadow: 0 0 3px #d5c4a1 !important;
}

menupopup menuitem
{
  -moz-appearance: none !important;
  background: transparent !important;
  text-shadow: 0 1px #fbf1c7 !important;
}

tooltip
{
  -moz-appearance: none !important;
  color: #282828 !important;
  background: rgba(0,0,0,.8) !important;
  border: 1px solid #d5c4a1 !important;
  border-radius: 3px !important;
}

/*----- FIX WHITE NEW TAB FLASH -----*/
tabbrowser tabpanels, #appcontent > #content
{
  background: #d5c4a1 !important;
}
