css:
@-moz-document url-prefix("https://drive.google.com") {

/* Hidden elements */
.a-da-gp, .a-da-SwCWWe, .a-Va-Zb-ut, .a-jn-Ma-nb-J {
    display: none;
}

/* Dark background */
.a-qc-La ,.a-D-B-x, .gb_Pd, .a-s-Ba-Ak, .a-D-B-WErN3d-j, .Kk7lMc-Yb-Il-j {
    background-color: #202020 !important;
}

.h-v-pc, .a-s-yd-Sa .a-jn-yd-J {
    background-color: #303030;
}

.gb_Te, .a-jn-yd, .h-uj, .a-w-x, .Yb-Il-d-c-j {
