.h-sb-Ic, .a-jn-yd, .a-jn-Ma-nb-J, .h-v-x {
    color: #878481 !important;
}

input.gb_ef, .a-v-T, .a-Va-Zb-M:not(.a-Va-Zb-wg-Qb), .g-d-wd {
    filter: invert(1);
}

/* Borders */

.a-Kk7lMc-ae3xF-j {
    border-color: #202020;
}

.a-s-Ba-Ak, .a-D-B-WErN3d-j, .Kk7lMc-Yb-Il-j, .a-da-Ul::before, .USrqEe-ml-Pr-qc-j::before, .h-Yo {
    border-color: #404040 !important;
}

/* Icons */
.a-Ba-Ed svg, .h-v-x svg, .a-qd-va, .Yb-Il-d-c {
    filter: invert(0.6) !important;
}

}
