css:
@-moz-document domain("mail.google.com") {
div#loading {
    background-color: BLACK;
/*     display:initial !important; */
}
div#loading div:nth-child(2) {
    filter: blur(1px);
    mix-blend-mode: hard-light;
}
div#nlpt {
    background-color: #4f908a3b;
}
div#loading div:nth-child(1) div:first-child {
    filter: blur(0px);
}

div#loading > div:first-child > div {
       animation: a-h .5s 1.25s 1 linear forwards,a-nt .6s 1.25s 1 cubic-bezier(0,0,.2,1) !important;
    background: #01534c73 !important;
    border-radius: 50%;
    height: 800px;
    left: 50%;
    margin: -448px -400px 0;
    position: absolute;
    top: 50%;
/*     transform: scale(0) !important; */
    width: 800px;
    filter: blur(70px) !important;
    opacity: 1 !important;
}
}
