css:
@-moz-document domain("tasvideos.org") {
#page, #pagepreview, #subpreviewwin, body, table.item th, table.tracker th, #page a:hover, a:focus, table.item td, div.toc, table.tracker td,
.gs-webResult .gs-snippet, .gs-imageResult .gs-snippet, .gs-fileFormatType {
    color: #ccc;
}

a.jsactionlink {
    color: #ccc !important;
}

#page a {
    color: #50a7ff;
}

body {
    background: #111;
}

.welcomediv {
    background-color: #371e37;
}

table.item td, div.toc, table.tracker td {
    background: #024;
}

table.item th, table.tracker th {
    background: #046;
}

table.item td.blah, .tabber_vert_live > ul.tabbernav li.tabberactive span.tablink:hover {
    background: black;
    color: white;
}

table.item td.shot {
    background: #222;
}

.tabber_vert_live > .tabbertab, .tabber_vert_live > ul.tabbernav li.tabberactive span.tablink {
    background: #151515;
}

ul.tabbernav li span.tablink:hover {
    color: white;
    background: #006;
    border-color: #aae;
}

.tabber_vert_live > ul.tabbernav li span.tablink {
    background: #113;
}

table.item td.sharing {
    background: #222;
    border-top: 1px solid #222;
}

.gsc-control-cse, .gsc-webResult.gsc-result:hover, .gsc-imageResult:hover, .gsc-webResult.gsc-result, .gsc-results .gsc-imageResult {
    border-color: #111;;
    background-color: #111;
}

.gs-webResult.gs-result a.gs-title:hover, .gs-webResult.gs-result a.gs-title:hover b, .gs-imageResult a.gs-title:hover, .gs-imageResult a.gs-title:hover b,
.gs-webResult.gs-result a.gs-title:link, .gs-webResult.gs-result a.gs-title:link b, .gs-imageResult a.gs-title:link, .gs-imageResult a.gs-title:link b {
    color: #aaaaff;
}

.gsc-results .gsc-cursor-box .gsc-cursor-page {
    border-color: #292929;;
    background-color: #111;
    color: #aaaaff;
}
}
