css:
@-moz-document domain("dvd.netflix.com") {
body, html {
    background: #333;
    color: #ccc;
}

#app, #body {
    background-color: #191919;
    color: #ccc;
}
.rated-titles li, .reviews li, .queue-lists li {
    background-color: #000;
    box-shadow: 0 1px 3px 0 #62666e;
}

.movie-list img {
    background-color: #686868;
}

#browseMenuContent {
    background-color: #2b2b2b;
}
#browseMenu ul:first-child {
    background-color: #000;
}

.tab-nav li a {
    color: #b8bbc1; /* #787b81 */
}

#app h1, .queue-lists h2, h2 {
    color: #ececec /*#2c2c2c*/;
}
#rated.row-list li > div, #header #browseMenu li a, .queue-lists li div {
    color: #a1a0a0 /*#616060*/;
}
h4 a, .queue-lists li div a.title {
    color: #fff;
}

a:hover {
    color: #5096f3;
}

#subnav.loaded {
    background-color: #141414;
}

#nav-links li a {
    color: #a1a0a0;
}
#nav-links li.selected a, #nav-links li a:hover {
    color: #fff;
}

.sort-and-filter > div {
    color: /*#434343*/ #b3b3b3;
}

.filter-box.active {
    background-color: #2f4345;
}

.filter-box img, .accordion-toggle, #sortMenu, #bmodal .bobarrow, #actionDialog #downarrow {
    filter: invert(100%);
    box-shadow: -2px 3px 3px -3px rgba(255,255,255,.28);
}

#bmodal {
    background-color: #000;
    box-shadow: 0 0 17px 4px rgba(255,255,255,0.18);
}
#bmodal .bobmeta span {
    color: #eaeaea;
}

#actionDialog {
    background-color: #000;
    box-shadow: 0 0 4px 0 rgba(255,255,255,0.28);
}
.input-spinner.input-group .input-group-addon, .spinner-container {
    background-color: #161616;
}
#actionDialog #moveToMenu .bottom {
    background-color: #191919;
}
.close {
    color: #fff;
    text-shadow: 0 1px 0 #000;
}
}
