@-moz-document domain("baidu.com") {
/*antiAD*/
.pe,.qx,#sma,#tbmp3,#mztop,#dMAï¼Œ
table[width="30%"][align="right"],
table[width="760"][height="90"],
table[id="0"],
table[id^="rightAd"],
div[id^="Tads"], 
div[id^=marqueebox],
div[id~=pma],
div[id~=ecomAdDiv],
a[href^="http://www.baidu.com/baidu.php?"],
