css:
@-moz-document url-prefix("http://gc-sportal/") {
.hidden-xs .pull-left {
 background-image: url(/*[[image]]*/) !important;   
 background-size: cover;
}

.navbar-brand .img-responsive {
    max-height:40px;
    content:url(https://mk0hive9wr7agvd1g6.kinstacdn.com/wp-content/uploads/2018/11/Gateshead-College-Logo-White-700x209.png)
}
.navbar-nav>li>a {
    color:#fff !important;
}
.tabselectedtitle {
    background-color:/*[[mainColour]]*/ !important;
}
.navbar-nav>li>a:hover {
    background-color:rgba(0, 0, 0, 0.05) !important;
}
.navbar-nav>li>a {
    background-color: /*[[mainColour]]*/ !important;
}
.jumbotron {
    background-color: #e6e6e6 !important;
}
.jumbotron div, .navbar-default .navbar-collapse {
    border-color: /*[[mainColour]]*/ !important;
}
.navbar-default .navbar-nav>.active>a, .navbar-default .navbar-nav>.active>a:hover, .navbar-default .navbar-nav>.active>a:focus {
    color: #fff !important;
    background-color: rgba(0, 0, 0, 0.1) !important;
}
.navbar-default, .btn-danger, .btn-primary {
    background-color: /*[[mainColour]]*/ !important;
    border-color: /*[[mainColour]]*/ !important;
    color: #fff !important;
}
.btn-gray, .btn-gray.disabled {
    background-color:/*[[mainColour]]*/ !important;
    border-color: /*[[mainColour]]*/ !important;
    color: #fff !important;
}
.btn-gray:hover, .btn-danger:hover, .btn-primary:hover {
    filter: brightness(85%);
}
.btn-success {
    background-color: #4aab6c !important;
    color: #fff !important;
}
.btn-success:hover {
    filter: brightness(85%);
}
.navbar-default .navbar-toggle {
    border-color: #2683b7 !important;
    background-color: #359cd6 !important;
}
.navbar-default .navbar-toggle:hover {
    background-color:#359cd6 !important;
}
.nav-pills>li.active>a, .nav-pills>li.active>a:hover, .nav-pills>li.active>a:focus {
    color: #fff !important;
    background-color:/*[[mainColour]]*/ !important;
}
a {
    color: /*[[mainColour]]*/ !important;
}
.panel-primary>.panel-heading, .label-primary {
    background-color: /*[[mainColour]]*/ !important;
    border-color: /*[[mainColour]]*/ !important;
}
.panel {
    border-color: /*[[mainColour]]*/ !important;
}
.k-state-selected>.k-link {
    color:#FFF!important;
}
.progress-bar-success {
    background-color: #5ad480 !important;
}
.table-bordered thead th, .table-bordered tfoot td {
    color: /*[[mainColour]]*/ !Important;
}
input[type=checkbox] {
    -webkit-appearance: none;
    -moz-appearance: none;
    appearance: none;
    display: inline-block;
    position: relative;
    background-color: #f3f3f3;
    color: #666;
    font-weight:700;
    top: 3px;
    margin-left:3px;
    height: 18px;
    width: 18px;
    border: 0;
    border-radius: 5px;
    cursor: pointer;
    margin-right: 7px;
    outline: none;
}
input[type=checkbox]:checked::before {
    position: absolute;
    font: 15px/1'Open Sans', sans-serif;
    left: 5px;
    top: 2px;
    content:'\02143';
    transform: rotate(40deg);
    color:/*[[mainColour]]*/;
}
.panel-gray>.panel-heading {
    background-color: #e6f3f7 !important;
    border-color: /*[[mainColour]]*/ !important;
    border-left: 1px solid /*[[mainColour]]*/ !important;
}
.article{
    border-width: 1px;
    border-bottom-style: dashed;
    padding-bottom: 20px;
    border-color: #cacaca;
}
}
