css:
@-moz-document domain("kahoot.it") {
:root {
    --main-bg-color: hsl(0, 0%, 0%);
    --main-fg-color: hsl(0, 0%, 100%);
    --triangle-color: hsl(350, 83%, 50%);
    --diamond-color: hsl(213, 83%, 50%);
    --circle-color: hsl(39, 98%, 54%);
    --square-color: hsl(110, 83%, 40%);    
    --triangle-color-darker: hsl(350, 83%, 40%);
    --diamond-color-darker: hsl(213, 83%, 40%);
    --circle-color-darker: hsl(24, 83%, 40%);
    --square-color-darker: hsl(110, 83%, 35%);
    --triangle-color-lighter: hsl(350, 83%, 60%);
    --diamond-color-lighter: hsl(213, 83%, 60%);
    --circle-color-lighter: hsl(24, 83%, 60%);
    --square-color-lighter: hsl(110, 83%, 50%);
}
.icon--kahoot-shape-arrow {
    fill: var(--triangle-color)
}
.icon--kahoot-shape-diamond {
    fill: var(--diamond-color)
}
.icon--kahoot-shape-circle {
    fill: var(--circle-color)
}
.icon--kahoot-shape-square {
    fill: var(--square-color)
}
.card--triangle {
    background-color: var(--triangle-color)
}
.card--triangle:hover {
    background-color: var(--triangle-color-darker)
}
.card--square {
    background-color: var(--square-color)
}
.card--square:hover {
    background-color: var(--square-color-darker)
}
.card--diamond {
    background-color: var(--diamond-color)
}
.card--diamond:hover {
    background-color: var(--diamond-color-darker)
}
.card--circle {
    background-color: var(--circle-color);
}
.card--circle:hover {
    background-color: var(--circle-color-darker)
}
.card--disabled {
    cursor: default;
    opacity: .75;
}
.placeholder--triangle {
    background: rgba(255, 51, 85, 0.175)
}
.placeholder--triangle:after {
    border-color: var(--triangle-color-lighter)
}
.placeholder--diamond {
    background: rgba(19, 104, 206, 0.175)
}
.placeholder--diamond:after {
    border-color: var(--diamond-color-lighter)
}
.placeholder--square {
    background: rgba(102, 191, 57, 0.175)
}
.placeholder--square:after {
    border-color: var(--square-color-lighter)
}
.placeholder--circle {
    background: rgba(216, 158, 0, 0.175)
}
.placeholder--circle:after {
    border-color: var(--circle-color-lighter)
}
.answers-histogram__label--triangle {
    color: var(--triangle-color)
}
.answers-histogram__label--square {
    color: var(--square-color)
}
.answers-histogram__label--diamond {
    color: var(--diamond-color)
}
.answers-histogram__label--circle {
    color: var(--circle-color)
}
.answers-histogram__label-icon--triangle {
    fill: var(--triangle-color)
}
.answers-histogram__label-icon--square {
    fill: var(--square-color)
}
.answers-histogram__label-icon--diamond {
    fill: var(--diamond-color)
}
.answers-histogram__label-icon--circle {
    fill: var(--circle-color)
}
body {
    background-color: var(--main-bg-color);
    font-family: "Montserrat", "Helvetica Neue", helvetica, arial, sans-serif;
    color: var(--main-fg-color)
}
.dgwErO {
    background: #020202;
}
.join-instructions .max-content .details .details-top {
    background-color: var(--main-bg-color);
}
.join-instructions .max-content .details .gameId {
    background-color: var(--main-bg-color);
}
.input-field__input {
    background-color: var(--main-bg-color);
}
mark {
    background: #ff0;
    color: #000
}
a {
    color: currentColor
}
.text-secondary {
    color: #737373
}
.loader {
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    -webkit-box-orient: vertical;
    -webkit-box-direction: normal;
    -ms-flex-direction: column;
    flex-direction: column;
    -webkit-box-align: center;
    -ms-flex-align: center;
    align-items: center;
    fill: #000;
    color: #000
}
.loader--light {
    fill: #ccc;
    color: #ccc
}
.loader--white {
    fill: #fff;
    color: #fff
}
.icon--active {
    border-color: #66bf39;
    color: #66bf39
}
.icon--inactive {
    border-color: #b2b2b2;
    color: #b2b2b2
}
.icon--invert {
    fill: #fff
}
.icon--facebook {
    color: #3b5998
}
.icon--googleplus {
    color: #dd4b39
}
.icon--pinterest {
    color: #cb2027
}
.icon--twitter {
    color: #00aced
}
.icon--done-page-edit {
    color: #45a3e5
}
.icon--done-page-preview {
    color: #45a3e5
}
.icon--done-page-play {
    color: #66bf39
}
.icon--done-page-share {
    color: #864cbf
}
.card {
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    -webkit-box-flex: 1;
    -ms-flex: 1 1 auto;
    flex: 1 1 auto;
    -webkit-box-align: center;
    -ms-flex-align: center;
    align-items: center;
    -webkit-box-pack: center;
    -ms-flex-pack: center;
    justify-content: center;
    position: relative;
    color: #fff;
    transition: box-shadow .2s, background .2s, opacity .2s
}
.card__icon {
    width: 50%;
    height: 50%;
    fill: #fff;
}
.button--blue {
    background: #45a3e5;
    color: #fff
}
.gfwkXD {
    background: #864cbf;
    color: #fff;
}
.show-media__button {
    -ms-flex-item-align: center;
    -ms-grid-row-align: center;
    align-self: center;
    width: 7.94rem;
    height: 100%;
    padding: 0;
    background: #333;
    border-radius: .125rem;
    font-size: .75rem;
    line-height: 2;
    color: #fff
}
.show-media__overlay {
    position: absolute;
    top: 0;
    bottom: 0;
    left: 0;
    right: 0;
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    -webkit-box-align: center;
    -ms-flex-align: center;
    align-items: center;
    -webkit-box-pack: center;
    -ms-flex-pack: center;
    justify-content: center;
    z-index: 40;
    width: 100%;
    height: 100%;
    margin: 0;
    padding: 5%;
    background: rgba(0, 0, 0, 0.7);
    pointer-events: auto
}
.show-media__close-button {
    position: fixed;
    top: .625rem;
    right: .625rem;
    width: 3.125rem;
    height: 3.125rem;
    padding: 0;
    background-color: #333;
    border-radius: 50%;
    font-size: .875rem;
    line-height: 1.5rem
}
.show-media__close-button svg {
    fill: #fff
}
.question-results__end-quiz-button {
    -webkit-box-flex: 1;
    -ms-flex: 1 1 4.25rem;
    flex: 1 1 4.25rem;
    -ms-flex-item-align: end;
    align-self: flex-end;
    margin: 0 1.25rem 0 0;
    font-family: 'Droid Sans', sans-serif;
    font-size: .813rem;
    font-weight: bold;
    text-align: right;
    color: #333
}
}
