css:
@-moz-document domain("rubyrealms.com")
{

.head {
    border-bottom: 2px solid #cc4b37;
}

.background-container {
    border-bottom: 2px solid #cc4b37;
}

#category-tabs a[aria-selected='true'] {
    background-color: #818181;
}

.tabs-content {
    border-bottom: 2px solid #cc4b37 !important;
}

.color-background-5 {
    border-bottom: 2px solid #cc4b37;
}

:root {
    --color-background-1: #e4e4e4;
    --color-background-1-rgba: rgba(236, 236, 236, 0.7);
    --color-background-2: #cecece;
    --color-background-2-rgba: rgba(156, 156, 156, 0.7);
    --color-background-xp: #c91022;
    --color-background-green: #14a76c;
    --font-color-1: #424242;
    --font-color-2: #000000;
    --font-color-3: #c91022;
}

.color-background-5 {
    background-color: #e4e4e4!important;
}

.shop-item {
    background-color: #cecece;
    height: 100%;
    overflow: hidden;
    position: relative;
    border-radius: 5px;
}

.item-info a {
    color: #424242;
}

.color-white {
    color: #424242!important;
}

#product-description span {
	color: #424242!important;
}

#product-updated span {
	color: #424242!important;
}

#product-created span {
	color: #424242!important;
}

#product-type span {
	color: #424242!important;
}

.color-primary-3 {
    color: #a08f00;
}

.footer-link {
    font-size: 20px;
    padding-top: 10px;
    padding-bottom: 10px;
    text-align: center;
    text-decoration: none;
    color: black !important;
}

.color-primary-1 {
    color: #333333;
}

#category-tabs a {
    background-color: #e4e4e4;
    box-shadow: 0px 0px 2px rgba(0,0,0,0.6);
    color: #424242;
    font-size: 18px;
    border-radius: 5px;
}

.font-color-0 {
    color: #424242;
}

.message-title {
    margin: auto;
    align-items: center;
    padding: 0;
    margin-bottom: 10px;
    color: #424242;
    font-size: 24px;
    border-bottom: 3px solid rgba(19,19,19,0.4);
}

.profile-about {
    width: 95%;
    margin: auto;
    padding: 5px 0px;
    color: #424242;
}

#profile-tabs a {
    color: #424242;
    font-size: 12px;
    text-align: center;
    font-weight: bold;
    padding: 20px 0px !important;
}

.wall-comment-title div {
	color: #424242!important;
}

.pagination a {
    color: #424242 !important;
}

.pagination .disabled {
    padding: 0.3rem 0.7rem;
    color: #8a8a8a;
    cursor: not-allowed;
}

.item-info {
    padding: 5px;
    color: #424242;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
    border-top: 1px solid rgba(19,19,19,0.4);
}

.dropdown-items a {
    color: #424242;
    display: block;
    padding: 5px 10px;
}

.dropdown-items a:hover {
    color: #424242;
    background-color: rgba(200,200,200,.1);
}

.reply-title {
    width: 100%;
    margin: auto;
    padding: 0px 0px 10px 0px;
    margin-bottom: 5px;
    color: #424242;
    font-size: 24px;
    border-bottom: 3px solid rgba(19,19,19,0.4);
}

.messages-title {
    margin: auto;
    padding: 5px 0px 5px 0px;
    margin-bottom: 5px;
    margin-top: 10px;
    color: #424242;
    font-size: 24px;
    border-bottom: 3px solid rgba(19,19,19,0.4);
}

.coming-motto {
    width: 100%;
    margin: auto;
    color: #424242;
    font-size: 20px;
}

.coming-title {
    width: 100%;
    margin: auto;
    padding: 10px 0px;
    color: #424242;
    font-size: 24px;
}

.news-title {
    color: #424242;
    font-size: 36px;
    padding: 15px;
}

.news-text {
    color: #424242;
    font-size: 24px;
    padding: 15px;
    text-align: justify;
}

.message-row-body {
	color: #424242!important;
}

.inbox-message {
    padding: 10px 10px;
    margin: 10px 0px;
    color: #424242;
    border-radius: 4px;
}

.inbox-message-time {
    color: #424242;
    font-weight: bold;
    font-size: 12px;
}

.profile-title {
    width: 95%;
    margin: auto;
    padding: 5px 0px;
    color: #424242;
    font-size: 24px;
    border-bottom: 3px solid rgba(19,19,19,0.4);
    align-items: center;
}

.members-title {
    color: #424242;
    font-size: 24px;
    border-bottom: 3px solid rgba(19,19,19,0.4);
    align-items: center;
}

.color-background-0 {
    background-color: #818181!important;
}

.auto div {
	color: #424242!important;
}

.group-title div a {
	color: #424242!important;
}

}
