css:
@-moz-document regexp("https://[^./]*\\.slack\\.com/(?!pricing)(?!security).*") {
:root {
    --black: rgba(0, 0, 0, 1);
    --black-97: rgba(0, 0, 0, 0.97);
    --black-90: rgba(0, 0, 0, 0.9);
    --black-80: rgba(0, 0, 0, 0.8);
    --black-75: rgba(0, 0, 0, 0.75);
    --black-70: rgba(0, 0, 0, 0.7);
    --black-50: rgba(0, 0, 0, 0.5);
    --black-40: rgba(0, 0, 0, 0.4);
    --black-25: rgba(0, 0, 0, 0.25);
    --black-20: rgba(0, 0, 0, 0.2);
    --black-15: rgba(0, 0, 0, 0.15);
    --black-125: rgba(0, 0, 0, 0.125);
    --black-11: rgba(0, 0, 0, 0.11);
    --black-10: rgba(0, 0, 0, 0.10);
    --black-08: rgba(0, 0, 0, 0.08);
    --black-05: rgba(0, 0, 0, 0.05);

    --twelve: rgba(12, 12, 15, 1);

    /* Primary color for background */
    --one: rgba(20, 20, 24, 1);
    --one-97: rgba(20, 20, 24, 0.97);
    --one-95: rgba(20, 20, 24, 0.95);
    --one-50: rgba(20, 20, 24, 0.5);
    --one-40: rgba(20, 20, 24, 0.4);
    --one-15: rgba(20, 20, 24, 0.15);
    --one-0: rgba(20, 20, 24, 0);

    --eleven: rgba(33, 33, 37, 1);

    --six: rgba(44, 49, 55, 1);
    --six-80: rgba(44, 49, 55, 0.8);
    --six-70: rgba(44, 49, 55, 0.7);

    --five: rgba(53, 53, 56, 1);
    --five-08: rgba(53, 53, 56, 0.08);

    --seven: rgba(72, 72, 75, 1);
    --seven-90: rgba(72, 72, 75, 0.9);
    --seven-80: rgba(72, 72, 75, 0.8);
    --seven-50: rgba(72, 72, 75, 0.5);
    --seven-25: rgba(72, 72, 75, 0.25);
    --seven-10: rgba(72, 72, 75, 0.10);

    --eight: rgba(119, 119, 121, 1);
    --eight-90: rgba(119, 119, 121, 0.9);
    --eight-80: rgba(119, 119, 121, 0.8);
    --eight-70: rgba(119, 119, 121, 0.7);
    --eight-60: rgba(119, 119, 121, 0.6);
    --eight-50: rgba(119, 119, 121, 0.5);
    --eight-40: rgba(119, 119, 121, 0.4);
    --eight-30: rgba(119, 119, 121, 0.3);
    --eight-25: rgba(119, 119, 121, 0.25);
    --eight-15: rgba(119, 119, 121, 0.15);
    --eight-10: rgba(119, 119, 121, 0.1);

    --three: rgba(137, 137, 139, 1);
    --three-80: rgba(137, 137, 139, 0.8);
    --three-60: rgba(137, 137, 139, 0.6);
    --three-15: rgba(137, 137, 139, 0.15);

    --ten: rgba(146, 146, 147, 1);

    --four: rgba(190, 190, 191, 1);

    /* Primary text color */
    --two: rgba(222, 222, 222, 1);
    --two-80: rgba(222, 222, 222, 0.8);
    --two-75: rgba(222, 222, 222, 0.75);
    --two-60: rgba(222, 222, 222, 0.6);
    --two-50: rgba(222, 222, 222, 0.5);
    --two-35: rgba(222, 222, 222, 0.35);
    --two-30: rgba(222, 222, 222, 0.3);
    --two-25: rgba(222, 222, 222, 0.25);

    --nine: rgba(242, 242, 242, 1);

    --white: rgba(255, 255, 255);
    --white-05: rgba(255, 255, 255, 0.05);
    --white-0: rgba(255, 255, 255, 0);


    --accent: rgba(3, 155, 229, 1);
    --accent-80: rgba(3, 155, 229, 0.8);
    --accent-75: rgba(3, 155, 229, 0.75);
    --accent-60: rgba(3, 155, 229, 0.6);
    --accent-40: rgba(3, 155, 229, 0.4);
    --accent-25: rgba(3, 155, 229, 0.25);
    --accent-20: rgba(3, 155, 229, 0.2);
    --accent-10: rgba(3, 155, 229, 0.1);

    /* 
  --mainBg:rgb(20, 20, 24);
  --txtIcns:rgb(222, 222, 222);
  --mnuPnls:rgb(44, 49, 55);
  --alrt:rgb(3, 155, 229); 
  */
}
* {
    transition: color 0.25s, background 0.25s;
}
body {
    background: var(--one);
    color: var(--two);
}
a {
    color: var(--three);
}
a:link,
a:visited {
    color: var(--three);
}
a:hover,
a:active,
a:focus {
    color: var(--four);
}
hr {
    border-bottom: 1px solid var(--five);
    border-top: 1px solid var(--one);
}
h1,
h2,
h3,
h4 {
    color: var(--two);
}
h1 a {
    color: var(--two);
}
h1 a:active,
h1 a:hover,
h1 a:link,
h1 a:visited {
    color: var(--two);
}
.bordered {
    border: 1px solid var(--six);
}
.top_border {
    border-top: 1px solid var(--six);
}
.bottom_border {
    border-bottom: 1px solid var(--six);
}
.left_border {
    border-left: 1px solid var(--six);
}
.right_border {
    border-right: 1px solid var(--six);
}
.bullet {
    color: var(--three);
}
.alert,
.c-alert,
.c-alert--boxed {
    background-color: var(--six);
    border-color: var(--black);
    color: var(--two);
    text-shadow: 0 1px 0 var(--black-50);
}
.alert h4,
.c-alert h4,
.c-alert--boxed h4 {
    color: var(--two);
}
.alert-info {
    background-color: var(--six);
    border-color: var(--black);
    color: var(--two);
}
.alert-info h4 {
    color: var(--two);
}
::-webkit-scrollbar-track {
    background: var(--six) !important;
    border-left-color: var(--six) !important;
    border-right-color: var(--six) !important;
    color: var(--six) !important;
}
::-webkit-scrollbar-thumb {
    background: var(--seven) !important;
    border-left-color: var(--six) !important;
    border-right-color: var(--six) !important;
    color: var(--one) !important;
}
::-webkit-scrollbar-corner {
    background: var(--one) !important;
}
.supports_custom_scrollbar #messages_container::after {
    background: none !important;
}
.monkey_scroll_bar {
    background: var(--five);
}
.monkey_scroll_handle_inner {
    background: var(--seven);
    border: 1px solid var(--eight);
}
.monkey_scroll_bar_native_scrollbar_shim {
    background: transparent;
}
#client-ui .monkey_scroll_bar {
    background: var(--five);
}
#client-ui .monkey_scroll_handle_inner {
    background: var(--seven);
    border: 3px solid var(--one);
}
.c-scrollbar--monkey .c-scrollbar__track {
    background: var(--five);
}
.c-scrollbar--monkey .c-scrollbar__bar {
    background: var(--seven);
    box-shadow: 0 3px 0 var(--one), 0 -3px 0 var(--one);
}
.client_channels_list_container {
    background-color: var(--six);
    border-right-color: var(--one);
}
#col_channels {
    color: var(--two);
}
.p-channel_sidebar {
    background-color: var(--six);
    color: var(--two);
}
.p-channel_sidebar__channel,
.p-channel_sidebar__channel:link,
.p-channel_sidebar__channel:visited,
.p-channel_sidebar__channel:hover,
.p-channel_sidebar__link,
.p-channel_sidebar__link:link,
.p-channel_sidebar__link:visited,
.p-channel_sidebar__link:hover {
    color: var(--two-80) !important;
}
.p-channel_sidebar__channel--selected,
.p-channel_sidebar__channel--selected:link,
.p-channel_sidebar__channel--selected:visited,
.p-channel_sidebar__channel--selected:hover,
.p-channel_sidebar__link--selected,
.p-channel_sidebar__link--selected:link,
.p-channel_sidebar__link--selected:visited,
.p-channel_sidebar__link--selected:hover {
    color: var(--two);
}
.p-channel_sidebar__channel:hover,
.p-channel_sidebar__link:hover {
    background: var(--one) !important;
}
.p-channel_sidebar__header {
    color: var(--two) !important;
}
.p-channel_sidebar__channel--im.p-channel_sidebar__channel--selected .c-presence,
.p-channel_sidebar__channel--im-slackbot.p-channel_sidebar__channel--selected::before {
    color: var(--two);
}
.p-channel_sidebar__channel--im .c-presence--away {
    color: var(--three);
}
/* .p-channel_sidebar__channel--selected,
.p-channel_sidebar__link--selected {
  background: var(--seven) !important;
}
.p-channel_sidebar__channel--selected:hover,
.p-channel_sidebar__link--selected:hover {
  background: var(--seven) !important;
}
.p-channel_sidebar__channel--selected::before,
.p-channel_sidebar__channel--selected:hover::before,
.p-channel_sidebar__channel--selected::after,
.p-channel_sidebar__channel--selected:hover::after {
  color: var(--two);
}
.p-channel_sidebar__link--selected::before,
.p-channel_sidebar__link--selected::after {
  color: var(--two);
} */
.p-channel_sidebar__badge,
.p-channel_sidebar__banner--mentions {
    background: var(--accent);
}
.p-channel_sidebar .c-custom_scrollbar__thumb_vertical,
.p-channel_sidebar .c-scrollbar__bar {
    background: var(--seven);
}
.p-channel_sidebar__channel--unread:not(.p-channel_sidebar__channel--muted):not(.p-channel_sidebar__channel--selected) .p-channel_sidebar__name,
.p-channel_sidebar__link--unread .p-channel_sidebar__name,
.p-channel_sidebar__link--invites:not(.p-channel_sidebar__link--dim) .p-channel_sidebar__name,
.p-channel_sidebar__section_heading_label--clickable:hover,
.p-channel_sidebar__quickswitcher:hover {
    color: var(--nine) !important;
}
.p-channel_sidebar__close_container:hover {
    background: var(--black);
}
.p-channel_sidebar__jumper {
    background: transparent !important;
}
.channels_list_holder h2 {
    color: var(--two) !important;
}
.channels_list_holder h2.hoverable:not(.jquery_hover):hover {
    color: var(--two);
    opacity: 0.8;
}
.channels_list_holder ul {
    color: var(--two) !important;
}
.channels_list_holder ul li {
    text-shadow: 0 1px 1px var(--black-15);
}
.channels_list_holder ul li .channel_name,
.channels_list_holder ul li .group_name,
.channels_list_holder ul li .im_name,
.channels_list_holder ul li .mpim_name,
.channels_list_holder ul li > a {
    background: var(--six);
    color: var(--two-80) !important;
}
.channels_list_holder ul li .channel_name:hover,
.channels_list_holder ul li .group_name:hover,
.channels_list_holder ul li .im_name:hover,
.channels_list_holder ul li .mpim_name:hover,
.channels_list_holder ul li > a:hover {
    background: var(--one) !important;
    border-bottom-right-radius: 0.25rem;
    border-top-right-radius: 0.25rem;
}
.channels_list_holder ul li .primary_action.im_name:hover .im_name_background,
.channels_list_holder ul li .primary_action.feature_user_custom_status:hover .im_name_background,
.channels_list_holder ul li .primary_action:not(.feature_user_custom_status):hover {
    background: var(--one);
}
.channels_list_holder ul li.mention a {
    color: var(--two);
}
.channels_list_holder ul li.unread:not(.muted_channel) .primary_action {
    color: var(--nine) !important;
}
.channels_list_holder ul li.unread .prefix {
    color: var(--two) !important;
    opacity: 1;
}
.channels_list_holder .group_close,
.channels_list_holder .im_close,
.channels_list_holder .mpim_close {
    color: var(--three) !important;
}
.channels_list_holder .unread_highlights {
    background: var(--accent);
    color: var(--two);
    text-shadow: 0 1px 0 var(--black-15);
}
.channels_list_holder .unread_msgs {
    background: var(--one);
    color: var(--two);
}
#channel_list_invites_link {
    border-bottom: 1px dotted var(--three);
    color: var(--three) !important;
    font-size: 0.9rem;
}
#channel_list_invites_link:hover {
    border-bottom: 1px solid var(--three);
}
#quick_switcher_btn {
    background: var(--six);
    border-top: 2px solid var(--six);
}
#quick_switcher_btn > i {
    color: var(--three);
}
#quick_switcher_btn:active,
#quick_switcher_btn:hover {
    background: var(--one);
    border-color: var(--one);
}
#quick_switcher_btn:active > i,
#quick_switcher_btn:hover > i {
    color: var(--three);
}
#quick_switcher_btn:active #quick_switcher_label,
#quick_switcher_btn:hover #quick_switcher_label {
    color: var(--three);
}
#quick_switcher_label {
    color: var(--three);
}
.p-channel_insights__channel .channel_link {
    color: var(--three);
}
.p-channel_insights__date_heading::before {
    background-color: var(--six);
}
.p-channel_insights__date_heading span {
    background-color: var(--one);
    color: var(--three);
}
.p-channel_insights__message--truncate::before {
    background: linear-gradient(180deg, transparent, var(--six) 90%);
}
.p-channel_insights__message ts-message.standalone:not(.for_mention_display):not(.for_search_display):not(.for_top_results_search_display):not(.for_star_display) {
    background-color: var(--one);
    border-color: var(--six);
}
.c-dialog__content {
    border-radius: 0.6rem;
}
.c-dialog__header,
.c-dialog__body,
.c-dialog__footer {
    background: var(--six);
}
.c-dialog__title {
    color: var(--two);
}
.c-dialog .p-custom_status_input__duration_picker_select {
    padding-left: 11px;
}
.c-dialog .p-custom_status_input__duration_picker_select .c-input_select__selected_value--placeholder {
    color: var(--two);
}
.loading #loading-zone {
    background: var(--one);
}
#loading_welcome {
    background-image: none;
}
#loading_message p {
    color: var(--two);
}
#loading_message #loading_message_attribution {
    color: var(--three);
}
#loading_team_menu_bg,
#loading_user_menu_bg {
    background: var(--one);
    border: none;
}
.infinite_spinner_bg,
.infinite_spinner_blue {
    stroke: var(--two);
}
body.loading #team_menu,
body.loading #quick_switcher_btn,
body.loading #team_menu_overlay,
body.loading #col_channels_overlay,
body.loading #col_channels {
    background-color: var(--six);
}
.p-degraded_list__loading {
    background-color: var(--one);
    color: var(--two);
}
input[type="text"],
input[type="password"],
input[type="datetime"],
input[type="datetime-local"],
input[type="date"],
input[type="month"],
input[type="time"],
input[type="week"],
input[type="number"],
input[type="email"],
input[type="url"],
input[type="tel"],
input[type="color"],
input[type="search"] {
    background-color: var(--seven);
    border-color: var(--black);
    color: var(--two);
}
input[type="text"]:focus,
input[type="password"]:focus,
input[type="datetime"]:focus,
input[type="datetime-local"]:focus,
input[type="date"]:focus,
input[type="month"]:focus,
input[type="time"]:focus,
input[type="week"]:focus,
input[type="number"]:focus,
input[type="email"]:focus,
input[type="url"]:focus,
input[type="tel"]:focus,
input[type="color"]:focus,
input[type="search"]:focus {
    border-color: var(--six-80);
    box-shadow: inset 0 1px 1px var(--black-075),
    0 0 8px var(--eight-60);
}
input[type="file"]:focus {
    outline: var(--two) dotted thin;
}
input[type="radio"]:focus,
input[type="checkbox"]:focus {
    outline: var(--two) dotted thin;
}
select {
    background: var(--seven);
}
select,
textarea {
    border: 1px solid var(--black);
    color: var(--two);
}
select:active,
select:focus,
textarea:active,
textarea:focus {
    border-color: var(--six);
    box-shadow: 0 0 7px var(--eight-15);
}
input:disabled,
input:disabled:active,
select:disabled,
textarea:disabled {
    border-color: var(--five) !important;
}
.no_touch input:hover,
.no_touch select:hover,
.no_touch textarea:hover {
    border-color: var(--six);
}
.no_touch label.select:hover select {
    border-color: var(--six);
}
.no_touch label.select:not(.disabled):hover::after {
    color: var(--six);
}
label.disabled {
    color: var(--two);
}
legend {
    border-bottom: 1px solid var(--eight);
}
legend small {
    color: var(--three);
}
.uneditable-input,
.uneditable-textarea {
    background-color: var(--five);
    border: 1px solid var(--black);
    color: var(--two);
}
.uneditable-input:focus,
.uneditable-textarea:focus {
    border-color: var(--eight-80);
    box-shadow: inset 0 1px 1px var(--black-075),
    0 0 8px var(--eight-60);
}
textarea {
    background-color: var(--seven);
    border: 1px solid var(--black);
    color: var(--two);
}
textarea:focus {
    border-color: var(--eight-80);
    box-shadow: inset 0 1px 1px var(--black-075),
    0 0 8px var(--eight-60);
}
::-webkit-input-placeholder {
    color: var(--two) !important;
    -webkit-filter: none;
    filter: none;
    opacity: 0.5;
}
::-moz-placeholder {
    color: var(--two) !important;
    filter: none;
    opacity: 0.5;
}
::placeholder {
    color: var(--two) !important;
    filter: none;
    opacity: 0.5;
}
[data-placeholder]:empty::before {
    color: var(--two) !important;
}
input::-webkit-input-placeholder,
textarea::-webkit-input-placeholder {
    color: var(--two) !important;
    -webkit-filter: none;
    filter: none;
    opacity: 0.5;
}
input::-moz-placeholder,
textarea::-moz-placeholder {
    color: var(--two) !important;
    filter: none;
    opacity: 0.5;
}
input::placeholder,
textarea::placeholder {
    color: var(--two) !important;
    filter: none;
    opacity: 0.5;
}
input[data-placeholder]:empty::before,
textarea[data-placeholder]:empty::before {
    color: var(--two) !important;
}
input[disabled],
input[readonly],
textarea[disabled],
textarea[readonly] {
    background-color: var(--seven) !important;
}
.form-actions {
    background-color: var(--five);
    border-top: 1px solid var(--six);
}
.help-block,
.help-inline {
    color: var(--three);
}
.input-append .add-on,
.input-prepend .add-on {
    background-color: var(--eight);
    border: 1px solid var(--seven);
    text-shadow: 0 1px 0 var(--black-15);
}
.btn {
    background-color: var(--seven);
    color: var(--two) !important;
    text-shadow: 0 1px 1px var(--black-15);
}
.btn.hover,
.btn:focus,
.btn:hover,
.btn.active,
.btn:active {
    background-color: var(--seven);
    color: var(--two);
}
.btn.btn_border {
    border: 2px solid var(--five);
}
.btn.disabled,
.btn:disabled {
    background-color: var(--black) !important;
}
.btn.disabled:active,
.btn.disabled:hover,
.btn:disabled:active,
.btn:disabled:hover {
    background-color: var(--black) !important;
}
.btn.btn_outline.btn_danger,
.btn.btn_outline.btn_warning {
    background-color: var(--accent) !important;
    color: var(--two) !important;
}
.btn.btn_outline.btn_danger:focus,
.btn.btn_outline.btn_danger:hover,
.btn.btn_outline.btn_warning:focus,
.btn.btn_outline.btn_warning:hover {
    background-color: var(--one) !important;
    border-color: var(--accent) !important;
    color: var(--accent) !important;
}
.btn.btn_outline.disabled {
    background: var(--five) !important;
    color: var(--three) !important;
}
.btn.btn_outline.disabled:hover {
    background: var(--five) !important;
    color: var(--three) !important;
}
.btn.btn_attachment {
    border-color: var(--seven);
}
.btn.btn_attachment:hover,
.btn.btn_attachment:focus {
    background-color: var(--six);
    border-color: var(--eight);
}
.btn.btn_attachment.btn_danger {
    border-color: var(--accent);
}
.btn.btn_attachment.btn_danger:hover,
.btn.btn_attachment.btn_danger:focus {
    border-color: var(--accent-80);
}
.btn.btn_attachment.btn_primary {
    border-color: var(--eight);
}
.btn.btn_attachment.btn_primary:hover,
.btn.btn_attachment.btn_primary:focus {
    border-color: var(--ten);
}
.btn_basic:focus,
.btn_basic:hover {
    color: var(--two);
}
.btn_outline {
    background: var(--one);
    color: var(--three) !important;
}
.btn_outline::after {
    border: 1px solid var(--five);
}
.btn_outline.btn_transparent {
    color: var(--seven-90) !important;
}
.btn_outline.btn_transparent::after {
    border: 1px solid var(--one-50);
}
.btn_outline.btn_transparent.active,
.btn_outline.btn_transparent.hover,
.btn_outline.btn_transparent:active,
.btn_outline.btn_transparent:focus,
.btn_outline.btn_transparent:hover {
    background-color: var(--seven-90) !important;
    color: var(--four) !important;
}
.btn_outline.btn_transparent.active,
.btn_outline.btn_transparent:active {
    background-color: var(--seven-80) !important;
}
.btn_outline.hover,
.btn_outline:focus,
.btn_outline:hover {
    background: var(--one);
    color: var(--four) !important;
}
.btn_outline:active {
    color: var(--four);
}
.btn_outline.active {
    color: var(--four) !important;
}
.btn_link {
    color: var(--three);
}
.btn_link:hover,
.btn_link:focus {
    color: var(--four);
}
.btn-group.open .btn.dropdown-toggle {
    background-color: var(--seven);
}
.btn-group.open .btn-primary.dropdown-toggle {
    background-color: var(--five);
}
.btn-group > .btn + .dropdown-toggle {
    box-shadow: inset 1px 0 0 var(--black-125),
    inset 0 1px 0 var(--black-20), 0 1px 2px var(--white-05);
}
.btn_info,
.btn.btn_success {
    background-color: var(--one) !important;
}
.btn_warning,
.btn_danger {
    background-color: var(--accent) !important;
