css:
@-moz-document domain("mewe.com") {
/* MEWE Dark Mode */

/*BEGIN 2019-03-07*/

/*Page header panel*/
.c-mw-app-main-header {
    background: #303030;
    background-color: rgb(48, 48, 48);
    box-shadow: 0 0 6px 0 rgba(0,0,0,.15);
}

/*Search box*/
.c-mw-app-main-header .header_search-input_wrapper .header_search-input {
    background-color: #000000;
    color: #c9c9c9;
}
.c-mw-app-main-header .header_search-input_wrapper .header_search-input:not(:focus) {
    border: 1px solid #5e6f80;
    border-top-color: rgb(94, 111, 128);
    border-right-color: rgb(94, 111, 128);
    border-bottom-color: rgb(94, 111, 128);
    border-left-color: rgb(94, 111, 128);
}

body {
    color: #c0c0c0;
    background: #1b1b1b;
    background-color: rgba(27, 27, 27, 1);
}

/*Left panel*/
.sidebar_box_header {
    color: #c0c0c0;
    background: #303030;
    background-color: rgb(48, 48, 48);
    border-bottom: solid 1px #1b1b1b;
    border-bottom-color: rgb(27, 27, 27);
}
.c-profile-overview h3 {
    color: #c0c0c0;
}
.c-field-editable.visible .value {
    margin-top: 3px;
    display: block;
    font-size: 13px;
    color: #c0c0c0;
    word-wrap: break-word;
    overflow: hidden;
}
.sidebar_box-bottom-corners {
    background-color: #303030;
}
.c-field-editable.visible {
    border-top: solid 1px #1b1b1b;
    border-top-color: rgb(27, 27, 27);
}

/*New post box at top*/
.c-mw-postbox-placeholder {
    background-color: #303030;
    box-shadow: 0 0 3px 0 rgba(0,0,0,0.05);
}
.c-mw-postbox .postbox_header {
    display: flex;
    padding: 11px 18px 11px 11px;
    border-radius: 6px;
    background-color: #404040;
}
/*Manual text in new post*/
.c-mw-postbox .postbox_header .postbox_header_post .postbox_header_textarea {
    color: #c0c0c0;
}
/*Enter new text footer*/
.postbox_controls, .postbox_controls--state-status{
    background-color: #404040;
}

/*Full screen transparant screen*/
.dialog_close-wrapper {
    background-color: rgba(30, 30, 30, 0.95);
}

/*Pop up dialog*/
.dialog_header {
    color: #292f33;
    background-color: #505050;
}
.simple-dialog .dialog_inner .dialog_content {
    background: #505050;
    background-color: rgb(80, 80, 80);
}
.dialog_inner {
    background-color: #505050;
    box-shadow: 0 0 3px 0 rgba(0,0,0,.05);
}
.simple-dialog .dialog_inner .btn-section.dark-footer {
    background: #505050;
    background-color: rgb(80, 80, 80);
}

