css:
@-moz-document domain("social.isurf.ca") {
/* Alter left/right column balance by changing 15%/85% widths */

/* Alter number of columns by changing 33% to desired */

#top > main > div.container { width:98%; }
#top > main > div.container > div > aside {
    width:15%;
    max-height: calc(65vh - 4em)!important;
}

#content { float:right; width:85%; }

#content > section.sectiontop.network-content-wrapper > div.tread-wrapper,
#content > section.sectiontop.community-content-wrapper > div.tread-wrapper,
#content > section.sectiontop.search-content-wrapper > div.tread-wrapper,
#content > section.sectiontop.profile-content-wrapper > div.tread-wrapper {
    vertical-align: top;
    display: inline-block !important;
    width: calc(33% - 10px);
    margin-right: 10px;
}

}
