css:
@-moz-document domain("youme.social") {
/* Change 25% to 33% for 3 columns */

#content-wrapper, #center, #content-wrapper.container,
#header > div.container.full_header { width: 99%!important; }
#leftnav { height: 77px; overflow: hidden; float:left; }
#right   { height: 42px; overflow: hidden; float:right; }
#leftnav:hover, #right:hover { height: 100%; }

/* DEFAULT MUSTARD THEME = list-content > li */
/* YOUME BLACK AND WHITE THEME = .feed-grid-item */

#list-content > li,
.feed-grid-item {
    position:static!important;
    width:calc(25% - 5px)!important;
    display: inline-block !important;
    vertical-align: top;
}

/* MOOPLUS THEME IS INHERENTLY SPLIT IN TWO */
/* ADJUST 50% TO 33% FOR SIX COLUMNS */

#list-content > div.slmooPlusRound > div.slFeedRight > li,
#list-content > div.slmooPlusRound > div.slFeedLeft > li {
    position:static!important;
    width:calc(50% - 5px)!important;
    display: inline-block !important;
    vertical-align: top;
}

}
