css:
@-moz-document url-prefix("https://politiken.dk/") {
body,
.frontpage__section,
.frontpage__section--top ~ .frontpage__section,
.article page__content,
.article,
.article__section--top ~ .article__section,
.u-bg--white{
    background-color: #1b1b1b !important;
    color: #ccc;
}
.u-bg--gray {
    background-color: #1b1b1b !important;
}
.card,
.topbar,
.mainmenu-button,
.footer {
    background-color: #333;
}
.headline {
    color: #ccc;
}
.pol-logo__svg {
    fill: #cf0000;
}
.topbar-logo__weekday-box {
    background-color: rgba(0,0,0,0);
    border: none;
}
.summary {
    color: #8e8e8e;
}
.newsletter-widget--topImage {
display: none !important;
}

.fact--has-showmore-js::after {
    background: linear-gradient(to bottom, rgba(0, 0, 0, 0) 0, rgba(0, 0, 0, 1) 64%, rgba(0, 0, 0, 1) 100%);
}
.fact__subheader, .fact__p {
    color: #ccc !important;
}
.ui-font--black {
color: #666;
}
.ui-font--gray-lighter {
    color: #272727;
}
.ad {
    display: none;
}
.list-item--numbered--left::before {
    color: #cf0000;
}
.topbar-nav__content::after {
    background-image: linear-gradient(to left, #323232, rgba(255,255,255,0));
}
.btn--outline--sales, .btn--outline--sales:visited {
	background-color: #323232;
}
.mainmenu {
    background-color: #1d1d1d;
}
.animateNwsltter2 {
    display: none !important;
}
.stopsign__article::before {
    background: unset;
}
.topbar-nav__item--head {
    background-color: #333333;
}
.byline {
    background-color: #1b1b1b;
    border: none;
}
.pagetools-button__link--facebook::before,
.pagetools-button__link--twitter::before,
.pagetools-button__link--mail::before,
.pagetools-button__link--present::before{
    background-color: #ccc;
    border: 1px solid #ccc;
}
.stopsign.stopsign__article.card {
    display: none;
}
}
