css:
@-moz-document domain("my.bible.com") {
.reader-header,
.yv-sticky-header-content.yv-sticky-header-fixed,
.yv-sticky-header-content.yv-sticky-header-hidden,
.version-picker-modal .version-list .version-header,
.plan-day-slider .slick-list,
.plan-nav-header,
.nav-content .plan-info .nav-title,
.nav-content .plan-info .nav-length,
div.devo-audio-disclaimer,
.moment-top,
.moment-content,
.moment-actions,
.moment.moment-vod.ng-isolate-scope,
.moment-comments-self-box,
.element-to-translate.yv-fullscreen-modal-content.dropdown-transform.down,
.card,
.bible-reader .note.show .body,
.element-to-translate,
.header,
.yv-lang-toggle {
    background: #333 !important;
    color: #fff !important;
    border: 0 !important;
    background-color: #333 !important;
}

.row,
body,
#react-app-Bible,
.vertical-center.circle-buttons,
.search-input,
.search.vertical-center.open.null,
.searchicon-container,
input,
#current-ui-view,
.carousel-banner .slide-title,
.carousel-standard .title,
.carousel-standard .slide-title,
.chapter-picker-modal .header,
.chapter-picker-modal,
.version-picker-modal .header,
.version-picker-modal,
.version-picker-modal .version-list,
.version-picker-modal .version-list li,
.plan-saved-title,
.plan-title,
.subscription-list .subscription .plan-length,
.subscription-actions span,
.plan-day-slider .day,
.subscription-show p,
.verse-action-footer,
.verse-selection,
.yv-button-bar-button,
.gray-background,
.yv-profile-menu.false,
.card,
.yv-profile-name,
.trigger-button.open,
.header.medium-8.small-11.centered.vertical-center {
    color: #fff !important;
    background: #212121 !important;
    border: 0;
}

.chapter-picker-modal .chapter-container .chapter-list li {
    color: #fff;
    background: #212121;
    border: 1px solid #555;
}

.yv-text-ellipsis,
h1.heading a {
    color: #fff;
}

.carousel-title .title-container {
    color: #fff !important;
    background: #333 !important;
    border: 0;
    border-radius: 0;
}

svg,
div.progress-bar,
.yv-profile-user-links a {
    filter: brightness(2);
}

button.slick-arrow,
.moment-actions-like.ng-scope,
.checkmark-container {
    filter: invert(1);
    color: #AF4895;
}

.circle-buttons {
    filter: invert(0);
}

.yv-icon-button span {
    filter: invert(1) brightness(2);
}

.yv-icon-button .yv-icon-button-svg svg {
    filter: invert(1) brightness(2);
}

.plan-day-slider .day.active {
    color: #fff !important;
    background: #6ab750 !important;
    border: 0;
}

.plan-day-slider .day h1 {
    color: #fff !important;
}

.plan-pieces li,
.plan-pieces li * {
    color: #fff !important;
    background: 0 !important;
    border: 0;
}

h3,
h2,
h1 {
    font-weight: bolder;
    color: #fff !important;
}

blockquote p {
    color: #fff;
    font-style: italic;
}

.plan-day-slider .day h4 div {
    border: 0 !important;
}

.plan-day-slider .day {
    background: #333 !important;
}
}
