css:
/* i really want this to be global */
#content-container 

{
    background-image: url(https://imgur.com/thimslw.png) ;
}
.input-fill 
{
    background-image: linear-gradient(#716e69, #625b5b);
}

form.status-message textarea {
}
.input-fill {
    background-image: linear-gradient(#ffffff, #cecece);
}

#mobile-page #profile-page

section.creations-custom .creation-list .creation-item .caption-container {
    bottom: -80px;
    height: 80px;
    width: 100%;
    position: absolute;
    background-color: rgba(255, 255, 255, 0.7);
    border-radius: 0 0 2px 2px;
}

#mobile-page #profile-page

section.creations-custom .creation-list .creation-item .caption {
    padding: 16px 16px 0;
    font-family: Open Sans,sans-serif;
    font-weight: 700;
    color: #747474;
    font-size: 115%;
    overflow: hidden;
    -o-text-overflow: ellipsis;
    text-overflow: ellipsis;
    text-transform: uppercase;
}

#mobile-page #profile-page

section.creations-custom .creation-list .creation-item .creation-stats {
    padding: 10px 16px;
    color: hsla(0, 0%, 36%, 0.53);
    font-size: 90%;
    font-family: Open Sans,sans-serif;
    font-weight: 700;
}

body#root-page-mobile header#pageheader {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    z-index: 1040;
    background-color: rgba(255, 255, 255, 0.95);
    height: 48px;
    -webkit-box-shadow: 0 1px 3px rgba(0,0,0,.4);
    box-shadow: 0 1px 3px rgba(0,0,0,.4);
    overflow: visible;
}

body#root-page-mobile header#pageheader nav.menu>ol>li a {
    display: block;
    line-height: 28px;
    padding: 10px 6px;
    font-size: .875rem;
    text-transform: uppercase;
    color: #b2b2b2;
    font-family: Open Sans,sans-serif;
    font-weight: 700;
}
.MrXCv {
    position: relative;
    overflow: visible;
    color: hsl(0, 0%, 70%);
    padding-top: 14px;
    padding-left: 10px;
    padding-bottom: 8px;
}

header#pageheader #meta-nav {
}
body {
    color: hsla(0, 0%, 100%, 1);
}

#header-icon {
    fill: hsl(0, 0%, 70%);
    margin-top: 16px;
    padding-left: 10px;
    cursor: pointer;
}
