}
#logout-button {
    font-size: 2vw
}
#login-area h2, #register-area h2, #recover-area h2 {
    font-size: 3vh;
    margin: 2vh 0 3vh 0;
}
form input[type="email"], form input[type="email"], form input[type="password"], form input[type="text"], form button {
