@-moz-document url("https://duelingnexus.com/login"), url("https://duelingnexus.com/register"), url("https://duelingnexus.com/recover"), url("https://duelingnexus.com/privacy_policy.html") {
/*SECTION 02 LOGIN*/
/*This section applies to the pages you can access without logging in.*/
#navbar-middle:before {
    display: none
}
#news-area {
    background-color: rgba(0, 0, 0, 0);
    height: 100%;
    width: 100%;
    max-width: 100vw;
    margin: 0;
    padding: 0;
    position: absolute;
}
#news-area:before, div#app:before {
    content: ' ';
    background: url('https://cdn.discordapp.com/attachments/670405796747673620/754166777587564655/Logo_Full.png') no-repeat center;
    background-size: auto 100%;
    height: 40%;
    width: 100%;
    display: block;
    opacity: 1;
    position: absolute;
    top: auto;
    right: 0;
    bottom: 30%;
    left: auto;
}
#socket-connection, #navbar-right, #news-area h3, #news-area p, #news-area strong, #news-area em {
    font-size: 0;
    opacity: 0;
}
#login-loading {
    font-size: 4vh;
    margin: 0;
    padding-top: 30%;
}
a {
    padding: 0;
    text-decoration: underline;
    opacity: .75;
}
a:focus {
    font-style: italic
}
a:hover {
    opacity: 1
}
h1, h2, h3 {
    font-variant: small-caps;
    line-height: 1;
    text-transform: none;
    -moz-user-select: none;
    -o-user-select: none;
    user-select: none;
    margin: .5vh;
    padding: 0;
}
#newsMain {
    height: 100vh;
    width: 100vw;
    position: relative;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
}
.news_list {
    margin: 5vh 2.5vw;
    height: 85vh;
    width: auto;
    max-width: 100%;
    overflow-y: scroll;
    position: absolute;
    top: 0;
}
#recover-area::before {
    content: "This feature is sometimes unavailable.";
    color: #F33;
}
/*This affects the navbar area while logged out. This smooths out the login animation.*/
