css:
@-moz-document domain("ficbook.net") {
:root {
	--black-translucent-color: #0000008a!important;
	--inner-bg-color: #000!important;
}
*, ::placeholder {
	color: #fff!important;
}
::selection {
	background: #fff!important;
	color: #000!important;
}
.tags-selector .list-of-categories .category, .pairing-highlight, .breadcrumb li:not(:last-child):after, .visit-link, label>.help, .profile-area ul li a, .achievement-group .achievement-item:hover {
	-webkit-text-fill-color: #fff!important;
}
a.pairing-highlight:hover, .tags-selector .list-of-categories .category:hover, label>.help:hover {
	-webkit-text-fill-color: #54aaff!important;
}
.header-t .add-nav a:hover, .nav-info ul .active a, .nav-info ul a:hover, .js-link:hover, .useractivities li>a span:hover, #footer .footer-nav a:hover, .categories-list ul>li>a[href]:hover, .help:hover, .wait-block .block .info strong:hover, .help, visit-link:hover, .list-unstyled>li>a:hover, .list-unstyled>li>div>a:hover, .js-open-notification-modal>span.text:hover, .fanfic-thumb-block .block .info strong:hover, .pairing-link:hover, .terms>[href], .setting-menu a:hover, .setting-menu .active {
	color: #54aaff!important;
}
.logo a>svg>path, .social-box-links>ul li a>svg>g>path {
	fill: #fff!important;
}
.dropdown-menu, .request-content, .useractivities .dropdown.open, .fanfic-hat .fanfic-hat-body, .fanfic-hat .fanfic-reward-container, .fanfic-download-option .fanfic-download-container, .social-auth-login a.btn-prevent, .social-networks>.btn-prevent, .if-you-were-premium, .data-table .row, #profileViewStats {
	background-image: -webkit-linear-gradient(0deg, #000, #000 0px)!important;
}
.social-auth-login a.btn-prevent:hover, .tags-selector .tag {
	background-image: -webkit-linear-gradient(0deg, #333, #333 0px)!important;
}
html, body, #searchDiv .search-input, .tooltip-inner, .tags .tag, .fandoms-block, .collections-block, .fanfics-and-originals-block, .requests-block, #searchDiv .common-search .show-all-button, .select2-dropdown, .form-control, .adult-warning .adult-warning-input, .smile-picker .dropdown-menu .get-premium-link a, .tags-selector .options-list, .modal-content {
	background: #000!important;
}
.btn, .book-container, .book-container .book-inner, #main, .header-holder, .navbar-default, .hero-block, .select2-container .select2-selection--multiple, .categories-block, .wait-block, .wait-block .block, .pairing-highlight, .request-area .request-thumb, .nav-info ul a, .fanfic-thumb-block .block, .fanfic-thumb-block, .pagenav input, .fanfic-main-info .badge-with-icon, .comment-container .comment-content, .like-badges, .smile-picker .dropdown-menu .get-premium-link, .img-holder-universal, .alert-danger, .author-hat, .fanfic-thumb-block .direction, .tags-selector .header-container, .tags-selector .list-of-categories .category, .fandom-selector .selected-fandoms .fandom, .find-form .form-group .sub-category, .well, .select2-container--default .select2-selection--single, .select2-container--default .select2-search--dropdown .select2-search__field, .badge, .chapter-info .list-of-fanfic-parts .part .part-link, .chapter-info .navigation-to-fanfic-parts-container .navigation-button, .panel-default>.panel-heading, .panel, .wait-block .direction, .tags-selector em, .header-m .navbar-toggle, .header-b .form-search .search-button, .profile-area ul li a, .header-t .dropdown.profile-holder.open button[data-toggle=dropdown], .setting-menu a:hover, .setting-menu .active, .help-box, .alert, .header-t .dropdown.profile-holder button[data-toggle=dropdown]:hover, .achievement-group, .achievement-item .progress, .achievement-group .achievement-item:hover, .achievement-item-detail .active-achievement-item, .achievement-item-detail .other-achievement-item, .main-holder table, .night-mode textarea, .alert-success, .if-you-were-premium.bg-stats, .fanfic-main-info .badge-with-icon.direction:before, .fanfic-inline, .fanfic-more-actions-btn {
	background: #0000!important;
}
.direction:before {
	background: #fff!important;
}
.btn:hover, .navbar-default .navbar-nav>li.active>a, .navbar-default .navbar-nav>li>a:focus, .navbar-default .navbar-nav>li>a:hover, .notice, .premium-notice span, .select2-container--default .select2-results__option--highlighted, .fanfic-main-info .badge-with-icon:hover, .smile-picker .list-smiles li:hover, .smile-picker .dropdown-menu .get-premium-link a:hover, .dropdown-menu .dropdown-menu-element:hover, .tags-selector .options-list li:not(.info):hover, .chapter-info .list-of-fanfic-parts .part .part-link:hover, .chapter-info .navigation-to-fanfic-parts-container .navigation-button:hover, .quoted, .fanfic-hat .fanfic-hat-body .fanfic-hat-premium-notice, #helloTabs li.active>a, #helloTabs li>a:hover, .profile-area ul li a:hover, .achievement-item .progress-bar, .fanfic-inline .hot-fanfic {
	background: #333!important;
}
.tags .tag.tag-adult:after, .tags-selector .tag.tag-adult:after {
	background: red!important;
}
.btn, .dropdown-menu, .form-control, .book-container .book-inner, .select2-container .select2-selection--multiple, #searchDiv .search-input, .wait-block .block, .tags .tag, .tooltip-inner, .request-area .request-thumb, .fanfic-thumb-block .block, .fandoms-block, .collections-block, .fanfics-and-originals-block, .requests-block, #searchDiv .common-search .show-all-button, .select2-dropdown, .pagenav input, .adult-warning .adult-warning-input, .fanfic-hat .fanfic-hat-body, .fanfic-hat .fanfic-reward-container, .fanfic-main-info .badge-with-icon, .fanfic-download-option .fanfic-download-container, .comment-container .comment-content, .like-badges, .smile-picker .dropdown-menu .get-premium-link a, .img-holder-universal, .tags-selector .header-container, .tags-selector .options-list, .fandom-selector .selected-fandoms .fandom, .well, .select2-container--default .select2-selection--single, .select2-container--default .select2-search--dropdown .select2-search__field, .badge, .chapter-info .list-of-fanfic-parts .part .part-link, .chapter-info .navigation-to-fanfic-parts-container .navigation-button, .panel, #helloTabs li>a, .header-m .navbar-toggle, .modal-content, .help-box, .alert, .if-you-were-premium, .achievement-group, .achievement-group .achievement-item, .achievement-item-detail .active-achievement-item, .achievement-item-detail .other-achievement-item, .night-mode textarea, .fanfic-inline {
	border: none!important;
	box-shadow: 0 0 0 1px #fff!important;
}
.main-holder table {
	border: 1px solid #fff!important;
}
.smile-picker .btn-smile-picker {
	box-shadow: none!important;
}
.profile-area ul li a:hover {
	border-color: #0000!important;
}
.tooltip.top .tooltip-arrow, .comment-container .comment-content:after, .select2-container--default .select2-selection--single .select2-selection__arrow b, .header-t .dropdown.profile-holder button[data-toggle=dropdown] .caret {
	border-top-color: #fff!important;
}
.select2-container--default.select2-container--open .select2-selection--single .select2-selection__arrow b {
	border-bottom-color: #fff!important;
}
#searchDiv .common-search .show-all-button:before, .like-badges:after {
	border-left-color: #fff!important;
}
#searchDiv .common-search .show-all-button:after {
	border-left-color: #000!important;
}
.navbar-default, .navbar-nav, .nav-tabs, .bottom-line, .help, .nav-info ul, .dropdown-menu.login-dropdown .block-separator, .wait-block .block .info strong, .fanfic-thumb-block .block .info strong, .js-link, .form-comments, .author-hat .nav-info, .find-form .form-group .sub-category, .pairing-selector .current-pairing .preview, .block-separator, .panel-heading, .profile-area ul li+li, .profile-area ul li ul li, .list-group-item, .table>tbody>tr>td, .table>tbody>tr>th, .alert-success {
	border: none!important;
}
.book-container>[class*="book-corner-"]:after, .book-container>[class*="book-corner-"]:before, .book-container>[class*="book-stiches-"]:after, .book-container>[class*="book-stiches-"]:before, .book-container .book-inner .pages, .hero-block #readerTab .list-of-popular, #toTop, .dark-theme-switcher, #showCornerCat, .share-buttons {
	display: none!important;
}
.form-control, .hero-block #readerTab .select2-search__field, .select2-container, .hero-block, .notice, .search-input, .direction:before, .select2-dropdown, .pagenav input, input:not([type=button]):not([type=submit]):not([type=reset]):not([type=checkbox]):not([type=radio]), .alert-danger, .direction .icon-direction, .modal-content, .help-box, .alert, .achievement-item .progress, .night-mode textarea, .alert-success, .fanfic-inline .hot-fanfic, .fanfic-more-actions {
	filter: none!important;
}
.useractivities li>a span, .header-t .dropdown.profile-holder button[data-toggle=dropdown] .text {
	text-decoration: none!important;
}
.social-networks>.btn-prevent, .profile-area ul li a {
	transition: none!important;
}
.tooltip.in, .smile-picker .btn-smile-picker img, .close, .badge-with-icon {
	opacity: 1!important;
}
.notice:after {
	border-color: transparent transparent transparent #333!important;
}
.comment_message.urlize>div.quoted {
	padding-top: 5px!important;
	padding-bottom: 5px!important;
	padding-left: 5px!important;
}
/* Серые "вкладки в книги" */
.navbar-default .navbar-nav>li>a:after, .navbar-default .navbar-nav>li>a:before {
	filter: invert(20%) sepia(37%) saturate(7%) hue-rotate(54deg) brightness(13%) contrast(67%)!important;
}
/* Логотип */
.header-b .logo img, .tag-spoiler-btn span {
	filter: invert(1) grayscale(1) brightness(8)!important;
}
input[type=checkbox], input[type=radio] {
	filter: invert(1) grayscale(1) brightness(5)!important;
}
.smile-picker .btn-smile-picker img {
	filter: invert(1)!important;
}
}
