css:
@-moz-document domain("cdromance.com") {
body, html, #content, .single-featured-image-header, input#search-form-1 ,.site-content-contain,.nav-subtitle{
    background-color: #333 !important;
    color: #fff !important;
    padding:0;
    margin:0;
    width:100% !important;
}
*:before, *:after {
    border:none !important;
    background-color: transparent !important;
}
.td-header-row, .td-header-menu-wrap, #top-menu, .navigation-top ,.site-footer{
    background-color: #000 !important;
}
.td-header-container, .td-ss-main-content, .td-header-main-menu.td-affix {
    width:100% !important;
}
.td-pb-span8 {
    width:60% !important
}
body, p {
    color: #ccc !important;
}
h1, h2, h3, h4, h5, h6, .post, .meta-info {
    color: #fff !important;
}
a,a span{
    color: #f22499 !important;
    text-decoration: none !important;
}
a:hover{
    color: #7472fe !important;
    text-decoration: none !important;
}
.td-container, .td-footer-container {
    background-color: rgba(0, 0, 0, .5) !important;
    width:100% !important;
}
.post {
    background-color: rgba(0, 0, 0, .5) !important;
    width:100% !important;
}
* {
    border:none !important;
}
button, .td-scroll-up-visible, button.acf-get-content-button, thead tr, .block-title span, .td-module-comments {
    color: #fff !important;
    background-color: #f22499 !important;
}
.social-share,
.td-module-comments a {
    color: #fff !important;
}
.crp_related li:hover, button:hover, .td-scroll-up-visible:hover, button.acf-get-content-button:hover, .td-module-comments:hover {
    background-color: #7472fe !important;
}
::-moz-selection {
    color: #fff !important;
    background: #f22499 !important;
}
.search-submit, #search .icon-search {
    color: #f22499 !important;
    background: transparent !important;
}
input#search-form-1 {
    padding:10px;
}
.social-share,#facebook,#twitter,#whatsapp,#reddit {
    
    color: #fff !important;
}
input::placeholder{
    color: rgba(256,256,226,.5) !important;
}
::selection {
    color: #fff !important;
    background: #f22499 !important;
}
::-webkit-scrollbar {
    width: 5px;
    height: 5px;
    opacity:.5;
}
::-webkit-scrollbar:hover {
    opacity:1;
}
/* Track */
 ::-webkit-scrollbar-track {
    background: #000;
}
/* Handle */
 ::-webkit-scrollbar-thumb {
    background: #f22499;
}
/* Handle on hover */
 ::-webkit-scrollbar-thumb:hover {
    background: #7472fe;
    cursor:pointer;
}
}
