input.form-submit:focus,
input#id_submitbutton:focus,
input#id_submitbutton2:focus,
.path-admin .buttons input[type="submit"]:focus,
td.submit input:focus,
.submit.buttons input[name="cancel"]:focus,
#notice .singlebutton + .singlebutton input:focus {
    background-color: var(--accent) !important;
}

.navbar .nav > li > a:hover,
.navbar .nav > li > a:focus,
.navbar .nav > li > a:active {
    background-color: var(--accent);
}

.navbar .nav li.dropdown.open > .dropdown-toggle {
    background-color: var(--accent) !important;
}

.navbar .dropdown-menu > li > a:hover,
.navbar .dropdown-menu > li > a:focus {
    background-color: var(--accent) !important;
}

#fgroup_id_buttonar {
    background: none !important;
    border: none !important;
    padding: 0 !important;
    margin-top: 0 !important;
}

fieldset.hidden > div > div.fdescription {
    display: none;
}

div.editor_atto_toolbar {
    background: #3A454B !important;
    border: none !important;
}

.editor_atto_toolbar div {
    border-radius: 0px !important;
    line-height: 13px !important;
    font-size: 13px !important;
    background: none !important;
    padding: 10px !important;
    border: none !important;
}

.editor_atto_toolbar > div * {
    padding: 0 !important;
    border: none !important;
    background: none !important;
    color: #aeafaf;
    margin-right: 2px !important;
    display: inline-block;
}

.editor_atto_toolbar button:hover {
    background: none !important;
}

.atto_group .atto_collapse_button > img {
    display: none !important;
}

.atto_group .atto_collapse_button::after {
    font-size: 22px !important;
    font-family: FontAwesome;
    font-weight: 100;
    content: "?";
}

.used-region-side-pre.used-region-side-post #region-main .mform:not(.unresponsive) .fitem .fitemtitle {
    display: none !important;
}

.editor_atto_toolbar div.toolbarbreak {
    display: none !important;
}

.submissionstatustable .box > div.submithelp {
    display: none;
}

.submissionstatustable .generaltable > tbody > tr * {
    border: none !important;
    background: none !important;
}

.submissionstatustable .generaltable > tbody > tr:nth-child(even) {
    background: #e2e2e2;
}

.course-search-result .paging {
    width: 49% !important;
    text-align: right;
}

.course-search-result .paging-showall {
    width: 49% !important;
    text-align: left !important;
}

.course-search-result .paging-showperpage {
    text-align: center !important;
    width: 100% !important;
}
