css:
@-moz-document domain("ecosia.org"), domain("blog.ecosia.org"), domain("ecosia.zendesk.com") {
body {
    color: #e8eaed;
    background-color: #1e1e1e;
    border-color: #1e1e1e;
}

html {
    background-color: #1e1e1e;
}

.search-section {
    background-color: #1e1e1e;
}

.typeahead {
    background-color: #3f3f45 !important;
    border: 1px solid #666;
    border-bottom: 0px;
}

.typeahead-suggestion:hover {
    background-color: #666 !important;
}

.typeahead-suggestion {
    border-bottom: 1px solid #666 !important;
}

.suggestion-list {
    background-color: #3f3f45 !important;
    border: 1px solid #666 !important;
    border-bottom: 0px;
}


.suggestion-link {
    background-color: #3f3f45 !important;
}

.suggestion-link:hover {
    background-color: #666 !important;
}

.search-section.section > div.search-background {
    background: linear-gradient(transparent 90%, #1e1e1e) bottom no-repeat, url(https://cdn.ecosia.org/indexpage/img/5a18884.png) bottom repeat-x !important;
}

section {
    background-color: #1e1e1e;
}

.search-section.section > div.content > div.logos-container > a > svg > g > path:nth-child(2) {
    fill: #e8eaed;
}

body > div.results-page > nav > div.container > div > div.col-lg-1.hidden-mobile.hidden-handheld > div > a > svg > g > path:nth-child(2) {
    fill: #e8eaed;
}

.c-nav {
    background-color: #1e1e1e;
}

.c-footer {
    background-color: #3f3f45 !important;
    border-top: 1px solid #666;
}

.c-footer__bottom {
    border-top: 1px solid transparent;
}

.c-footer__copyright {
    color: #e8eaed !important;
}

.c-widget {
    border-color: #666;
}

.c-btn {
    background-color: #3f3f45 !important;
    color: #e8eaed !important;
    border: 1px solid #666;
}

.c-tags a {
    background-color: #3f3f45 !important;
}

hr {
    background-color: #666 !important;
}

.c-related {
    background-color: #1e1e1e !important;
    border-top: 1px solid #666;
}

.c-related .c-post-card {
    background-color: #3f3f45 !important;
    border: 1px solid #666;
}

.c-post-hero__content {
    background-color: #3f3f45 !important;
    color: #e8eaed !important;
    border-color: #666;
}

.c-archive {
    background-color: #3f3f45 !important;
    color: #e8eaed !important;
    border: 2px solid #666;
}

.notifications-banner {
    background-color: #666 !important;
}

.search-header {
    background-color: #3f3f45 !important;
}

.navbar-row {
    background-color: #1e1e1e !important;
}

.navbar-link,
.navbar-link:active,
.navbar-link:hover,
.navbar-link:visited {
    color: #e8eaed !important;
}

.svg-icon > g > g {
    fill: #e8eaed !important;
}

.results {
    background-color: #1e1e1e !important;
}

.result-wrapper {
    background-color: #1e1e1e !important;
}

.entity,
.sidebar-product-ad {
    background-color: #3f3f45 !important;
    -webkit-box-shadow: 0 2px 6px 0 transparent !important;
    box-shadow: 0 2px 6px 0 transparent !important;
}

.entity-title {
    color: #e8eaed !important;
}

.result-snippet,
.sidebar-text {
    color: #e8eaed !important;
}

a {
    color: #b8cbe8 !important;
}
a:visited {
    color: #b8cbe8 !important;
}

.thumbnail-item {
    background-color: #3f3f45 !important;
    -webkit-box-shadow: 0 2px 6px 0 transparent !important;
    box-shadow: 0 2px 6px 0 transparent !important;
}

.thumbnail-title,
.thumbnail-news .result-url:visited .thumbnail-title,
.thumbnail-video .result-url:visited .thumbnail-title {
    color: #b8cbe8 !important;
}

.thumbnail-publisher {
    color: #e8eaed !important;
}

.thumbnail-video-duration {
    color: #e8eaed !important;
}

.card-title {
    color: #d5d5d5 !important;
}

.thumbnail-description {
    color: #d5d5d5 !important;
}

.entity-subtitle {
    color: #d5d5d5 !important;
}

.tag {
    background-color: #3f3f45 !important;
}

.flags-dropdown-arrow {
    background-color: #3f3f45 !important;
    border-right: 1px solid #666;
    border-bottom: 1px solid #666;
    -webkit-box-shadow: 0 2px 6px 0 transparent !important;
    box-shadow: 0 2px 6px 0 transparent !important;
}

.flags-dropdown-header {
    background-color: #3f3f45 !important;
    border-bottom: 1px solid #666;
}

.flags-dropdown-body {
    background-color: #3f3f45 !important;
}

.dropdown-menu-group {
    background-color: #3f3f45 !important;
    border-bottom: 1px solid #666;
}

.dropdown-link:hover {
    background-color: #666 !important;
    border-bottom: 1px solid #666;
    color: #e8eaed;
}

.dropdown-menu {
    background-color: #3f3f45 !important;
    -webkit-box-shadow: 0 2px 6px 0 transparent !important;
    box-shadow: 0 2px 6px 0 transparent !important;
    border: 1px solid #666 !important;
}

.filter-trigger {
    background-color: #3f3f45 !important;
    color: #e8eaed !important;
}

.filter-trigger:hover {
    background-color: #666 !important;
    color: #e8eaed !important;
}

.pagination-button:hover {
    background-color: #666 !important;
    color: #e8eaed !important;
    background-image: none;
    border-radius: 50%;
}

.pagination-next,
.pagination-prev {
    background-color: #3f3f45 !important;
    color: #e8eaed !important;
    background-image: none;
    border-radius: 50%;
}
.pagination-button:visited {
    color: #e8eaed !important;
}

a.pagination-button.pagination-prev.enabled > svg > g > g {
    fill: #e8eaed !important;
}

a.pagination-button.pagination-next.enabled > svg > g > g {
    fill: #e8eaed !important;
}

.pagination-next.enabled:focus,
.pagination-next.enabled:hover,
.pagination-prev.enabled:focus,
.pagination-prev.enabled:hover {
    background-color: #666 !important;
    color: #e8eaed !important;
    background-image: none;
    border-radius: 50%;
}

.filter-list {
    background-color: #3f3f45 !important;
    color: #e8eaed !important;
}

.statistics-what {
    background-color: #3f3f45 !important;
    color: #e8eaed !important;
}

.reports {
    background-color: #3f3f45 !important;
}

.jobs {
    background-color: #3f3f45 !important;
}

.team-member-name {
    color: #e8eaed !important;
}

.statistics {
    background-color: #3f3f45 !important;
}

.statistics-value-description {
    color: #e8eaed !important;
}

.statistics-value-highlight {
    color: #e8eaed !important;
}

.h2,
.text-intro {
    color: #e8eaed !important;
}

.filter-link:focus,
.filter-link:hover {
    background-color: #666 !important;
    color: #e8eaed !important;
}

.how-text {
    color: #e8eaed !important;
}

.privacy-claims-text {
    color: #e8eaed !important;
}

.privacy-faq-answer {
    color: #e8eaed !important;
}

.privacy-claims {
    background-color: #3f3f45 !important;
}

.privacy-policy h2 {
    color: #e8eaed !important;
    text-decoration: underline !important;
}

.privacy-policy .read-more {
    background-image: -webkit-gradient(linear, left top, left bottom, from(rgba(255, 255, 255, 0)), color-stop(20%, #fff), to(#fff));
    background-image: -webkit-linear-gradient(top, rgba(255, 255, 255, 0) 0%, #3f3f45 20%, #3f3f45 100%);
    background-image: linear-gradient(to bottom, rgba(255, 255, 255, 0) 0%, #3f3f45 20%, #3f3f45 100%);
    border-bottom: 1px solid #666;
}

.settings-headline {
    color: #e8eaed !important;
}

.form-with-labels {
    color: #e8eaed !important;
}

#footer > div > div:nth-child(1) > div:nth-child(1) > a > svg > g {
    fill: #e8eaed !important;
}


.custom-select-chosen {
    color: #e8eaed !important;
}

.result-metadata {
    color: #e8eaed !important;
}

.popover-section {
    background-color: #3f3f45 !important;
    border: 1px solid #666 !important;
}

.notifications-center-container > .notifications-center-child,
.notifications-popup-container {
    border: 1px solid #666 !important;
}

.notifications-item {
    background-color: #3f3f45 !important;
    border-bottom: 1px solid #666 !important;
}

.notifications-item:hover {
    background-color: #666 !important;
}

.notifications-center-more {
    background-color: #3f3f45 !important;
    border-top: 1px solid #666 !important;
}

.notifications-center-more:hover {
    background-color: #666 !important;
}

.flags-dropdown-more:active,
.flags-dropdown-more:focus,
.flags-dropdown-more:hover,
.flags-dropdown-more:visited,
.link-more:active,
.link-more:focus,
.link-more:hover,
.link-more:visited,
.popover-action:active,
.popover-action:focus,
.popover-action:hover,
.popover-action:visited,
.popover-result .popover-text > a:active,
.popover-result .popover-text > a:focus,
.popover-result .popover-text > a:hover,
.popover-result .popover-text > a:visited {
    background-color: #3f3f45 !important;
}

.entity-images-more:after,
.pill-arrow-content,
.pill:after,
.section-title-pill:after {
    border-bottom: 11px solid transparent;
}

.flags-dropdown-chosen {
    border-bottom: 1px solid #666 !important;
}

.flags-dropdown-more {
    border-top: 1px solid #666 !important;
}

.info-section {
    background: url(data:image/svg+xml;base64,) bottom no-repeat !important;
    background-size: cover !important;
    background-color: #1e1e1e !important;
}

.social-section[data-v-3952deb5] {
    background: url(data:image/svg+xml;base64,) no-repeat top;
    background-size: cover;
    background-color: #1e1e1e !important;
    text-align: center;
    color: #e8eaed !important;
    padding-top: 10vw;
    padding-bottom: 50px;
    font-family: Campton, Helvetica, Arial, sans-serif;
}

body > nav > div.search-header-opaque > div.container > div > div.col-lg-1.hidden-mobile.hidden-handheld > a > svg > g > path:nth-child(2) {
    fill: #e8eaed !important;
}

.dropdown-menu [role="menuitem"][aria-selected="true"] {
    background-color: #3f3f45 !important;
}

.dropdown-menu [role="menuitem"]:hover {
    background-color: #666 !important;
}

.index-footer {
    background-color: #3f3f45 !important;
}

.small-footer {
    background-color: #3f3f45 !important;
}

.site-footer {
    background-color: #3f3f45 !important;
}

.site-footer:before {
    background-color: #1e1e1e !important;
}

.nav-social-link svg [fill="#000"] {
    fill: rgb(54, 172, 184) !important;
}
}
